query SpacePermissionsAnonymousQuery($spaceKey: String!) {
  spacePermissions(spaceKey: $spaceKey) {
    filteredSubjectsWithPermissions(
      permissionDisplayType: ANONYMOUS
      first: 25
      after: ""
      filterText: ""
    ) {
      nodes {
        permissions
        filteredPrincipalSubjectKey {
          id
          displayName
        }
      }
    }
  }
}

query usePublicLinkSpaceTogglePublicLinkSpaceQuery($spaceId: ID!) {
  publicLinkSpace(spaceId: $spaceId) {
    name
    spaceId
    spaceKey
    spaceAlias
    status

    stats {
      publicLinks {
        active
      }
    }

    isPolicySetForClassificationLevel
  }

  publicLinkSiteStatus {
    status
  }
}

assignments.value:
={{
$json.pagesWithPublicLink.data.publicLinksByCriteria.nodes.map(p => ({
      id: p.id,
      title: p.title,
      status: p.status,
      publicUrl: $('Set Variables').first().json.atlassianDomain+p.publicLinkUrlPath,
      lastEnabledBy: p.lastEnabledByUser
    }))
}}
