const wf = { ...$input.first().json };

// remove volatile fields
delete wf.createdAt;
delete wf.updatedAt;

// stable stringify (important for consistent hash)
const content = JSON.stringify(wf);

return [
  {
    json: {
      workflowId: wf.id,
      workflowName: wf.name,
      workflowJson: wf,
      content
    }
  }
];

const errors = [];
const items = $input.all();
const n8nHost = items[0].json.n8nHost;
const apiKey = items[0].json.apiKey;
const folderPath = items[0].json.backupFolder;
const dtName = items[0].json.dataTableTitle;
const backupScope = items[0].json.backupScope;
const requiredTag = items[0].json.requiredTag;


if (n8nHost == '') {
  errors.push('A valid n8n hostname must be configured.');
}
if (apiKey == '') {
  errors.push('A valid n8n ApiKey must be configured.');
}
if (folderPath == '') {
  errors.push('Google drive directory path must be configured.');
}
if (dtName == '') {
  errors.push('N8n data table name must be configured.');
}
if (backupScope == '') {
  errors.push('The backup scope must be configured.');
}
if (backupScope == 'tagged' && requiredTag == '') {
  errors.push('Please specify the tag name if you have configured the backup scope as ‚Äútagged‚Äù.');
}

if (errors.length) {
  throw new Error(`Validation failed: ${errors.map((e, i) => `${i+1}. ${e}`).join(' | ')}`);
}

return items;
