const coin = $input.item.json;

const symbol = coin.symbol.toUpperCase();
const priceChange24h = coin.price_change_percentage_24h || 0;
const currentPrice = coin.current_price;
const marketCap = coin.market_cap;

let signal;
if (priceChange24h <= -2) {
  signal = 'BUY';
} else if (priceChange24h >= 2) {
  signal = 'SELL';
} else {
  signal = 'HOLD';
}

const redisKey = `crypto:signal:${symbol}`;

return {
  json: {
    symbol,
    name: coin.name,
    currentPrice,
    priceChange24h: priceChange24h,
    marketCap,
    signal,
    redisKey,
    timestamp: new Date().toISOString()
  }
};

return [{
  json: {
    message: `üî¥ SELL SIGNAL CONFIRMED

Coin: ${$json.symbol}
24h Change: ${$json.priceChange24h}%
Time: ${$json.timestamp}

‚ö†Ô∏è Strong upward move detected`
  }
}];
