const body = {
  model: "llama-3.3-70b-versatile",
  messages: [
    {
      role: "system",
      content: "You are a senior product analyst assisting product and leadership teams. You convert raw customer complaints into clear, actionable product feedback. Be professional, concise, and insight-focused. Do not sound like an AI."
    },
    {
      role: "user",
      content: `
PRODUCT NAME: ${$input.item.json.product_name}
CUSTOMER NAME: ${$input.item.json.customer_name}
COMPLAINT COUNT: ${$input.item.json.complaint_count}
SOURCE: ${$input.item.json.source || "support"}
RAW CUSTOMER COMPLAINTS:
${$input.item.json.reason}

TASK:
Analyze the above customer complaints and generate structured product feedback.

OUTPUT REQUIREMENTS:
- Identify overall sentiment (Positive / Neutral / Negative)
- Identify main problem themes (Billing, Product Quality, Support, UX, Performance, Feature Request, Other)
- Explain the root cause in simple business language
- Provide 2â€“3 clear, actionable product improvement suggestions
- Write in an executive-ready tone
- Do NOT mention AI or the word "complaint"
- Keep it concise and clear
- Return only the final feedback text (no headings, no markdown)
`
    }
  ],
  temperature: 0.5,
  max_tokens: 220
};

// IMPORTANT: Store original data in a separate field so HTTP Request doesn't overwrite it
return {
  json: {
    requestBody: body,
    // Preserve ALL original customer data here
    originalCustomerData: {
      row_number: $input.item.json.row_number,
      customer_id: $input.item.json.customer_id,
      customer_name: $input.item.json.customer_name,
      email: $input.item.json.email,
      product_name: $input.item.json.product_name,
      payment_status: $input.item.json.payment_status,
      complaint_count: $input.item.json.complaint_count,
      risk_level: $input.item.json.risk_level,
      reason: $input.item.json.reason,
      action_required: $input.item.json.action_required,
      notification_status: $input.item.json.notification_status,
      logged_at: $input.item.json.logged_at
    }
  }
};

// Get the AI response
const aiResponse = $input.item.json.choices[0].message.content;

// Get the preserved original customer data
const originalData = $('Prompt For Model1').first().json.originalCustomerData

// Merge everything together
return {
  json: {
    // Spread all original customer data
    ...originalData,

    // Add the AI-generated feedback
    ai_feedback: aiResponse,
    feedback_generated_at: new Date().toISOString()
  }
};

// Get all items from the node BEFORE notification
const previousItems = $items('Merge Risk Result');

// Add notification status to each item
return previousItems.map(item => {
  return {
    json: {
      ...item.json,
      notification_status: 'sent',
      logged_at: new Date().toISOString()
    }
  };
});

