// Prepare story data for image generation
const response = $input.first().json;
const formData = $('Validate Input').first().json;
const imageStyle = formData['Image Style'];

const story = JSON.parse(response.choices[0].message.content);

function sanitize(text) {
  if (!text) return '';
  return text
    .replace(/\\/g, '\\\\')
    .replace(/"/g, "'")
    .replace(/\n/g, ' ')
    .replace(/\r/g, '')
    .replace(/\t/g, ' ')
    .replace(/[\x00-\x1F\x7F]/g, '')
    .trim();
}

const preparedScenes = story.scenes.map((scene, index) => {
  const dallePrompt = `${imageStyle}, children's book illustration: ${sanitize(scene.imagePrompt)}. High quality, vibrant colors, no text in image.`;

  return {
    ...scene,
    sceneIndex: index,
    dallePrompt: dallePrompt.substring(0, 4000),
    imageUrl: null
  };
});

return {
  json: {
    story: { ...story, scenes: preparedScenes },
    formData: formData,
    storyId: formData.storyId,
    totalScenes: preparedScenes.length,
    createdAt: new Date().toISOString()
  }
};

// ========================================
// GENERATE IMAGES WITH DALL-E 3
// ========================================
// Update OPENAI_API_KEY with your key or use credentials
// ========================================

const data = $input.first().json;
const scenes = data.story.scenes;

// ‚ö†Ô∏è REPLACE WITH YOUR API KEY
const OPENAI_API_KEY = 'YOUR_OPENAI_API_KEY';

const processedScenes = [];

for (let i = 0; i < scenes.length; i++) {
  const scene = scenes[i];

  try {
    const response = await fetch('https://api.openai.com/v1/images/generations', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${OPENAI_API_KEY}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        model: 'dall-e-3',
        prompt: scene.dallePrompt,
        n: 1,
        size: '1024x1024',
        quality: 'standard',
        response_format: 'url'
      })
    });

    const result = await response.json();

    if (result.error) {
      processedScenes.push({
        ...scene,
        imageUrl: `https://placehold.co/1024x1024/ef4444/ffffff?text=Error`,
        imageError: result.error.message
      });
    } else {
      processedScenes.push({
        ...scene,
        imageUrl: result.data[0].url,
        imageGeneratedAt: new Date().toISOString()
      });
    }

    // Rate limit: wait 1 second between requests
    if (i < scenes.length - 1) {
      await new Promise(resolve => setTimeout(resolve, 1000));
    }

  } catch (error) {
    processedScenes.push({
      ...scene,
      imageUrl: `https://placehold.co/1024x1024/ef4444/ffffff?text=Error`,
      imageError: error.message
    });
  }
}

return {
  json: {
    story: { ...data.story, scenes: processedScenes },
    formData: data.formData,
    storyId: data.storyId,
    totalScenes: data.totalScenes,
    createdAt: data.createdAt
  }
};

// ========================================
// UPLOAD IMAGES TO FIREBASE STORAGE
// ========================================
// Update FIREBASE_BUCKET with your bucket name
// ========================================

const data = $input.first().json;
const scenes = data.story.scenes;
const storyId = data.storyId;

// ‚ö†Ô∏è REPLACE WITH YOUR FIREBASE BUCKET
const FIREBASE_BUCKET = 'your-project.appspot.com';

const uploadedScenes = [];

for (let i = 0; i < scenes.length; i++) {
  const scene = scenes[i];

  if (scene.imageError || !scene.imageUrl || scene.imageUrl.includes('placehold.co')) {
    uploadedScenes.push(scene);
    continue;
  }

  try {
    // Download from DALL-E
    const imageResponse = await fetch(scene.imageUrl);
    const imageBuffer = await imageResponse.arrayBuffer();

    // Upload to Firebase
    const storagePath = `stories/${storyId}/scene_${i}.png`;
    const encodedPath = encodeURIComponent(storagePath);

    const uploadResponse = await fetch(
      `https://firebasestorage.googleapis.com/v0/b/${FIREBASE_BUCKET}/o?name=${encodedPath}`,
      {
        method: 'POST',
        headers: { 'Content-Type': 'image/png' },
        body: Buffer.from(imageBuffer)
      }
    );

    const uploadResult = await uploadResponse.json();

    let firebaseUrl = uploadResult.downloadTokens
      ? `https://firebasestorage.googleapis.com/v0/b/${FIREBASE_BUCKET}/o/${encodedPath}?alt=media&token=YOUR_TOKEN_HERE
      : `https://firebasestorage.googleapis.com/v0/b/${FIREBASE_BUCKET}/o/${encodedPath}?alt=media`;

    uploadedScenes.push({ ...scene, imageUrl: firebaseUrl });

  } catch (error) {
    uploadedScenes.push(scene);
  }
}

return {
  json: {
    story: { ...data.story, scenes: uploadedScenes },
    formData: data.formData,
    storyId: data.storyId,
    totalScenes: data.totalScenes,
    createdAt: data.createdAt
  }
};

// Finalize story object
const data = $input.first().json;

const finalScenes = data.story.scenes.map(scene => {
  const { sceneIndex, dallePrompt, imageError, ...cleanScene } = scene;
  return cleanScene;
});

const finalStory = {
  id: data.storyId,
  title: data.story.title,
  titleEnglish: data.story.titleEnglish,
  summary: data.story.summary,
  summaryEnglish: data.story.summaryEnglish,
  language: data.story.language,
  targetAudience: data.story.targetAudience,
  mood: data.story.mood,
  moral: data.story.moral,
  imageStyle: data.formData['Image Style'],
  totalScenes: finalScenes.length,
  scenes: finalScenes,
  characters: data.story.characters,
  metadata: {
    createdAt: data.createdAt,
    updatedAt: new Date().toISOString(),
    generator: 'AI Story Generator',
    originalPrompt: data.formData['Story Topic']
  }
};

return {
  json: {
    finalStory: finalStory,
    language: data.formData.Language.toLowerCase().replace(' ', '_')
  }
};

// ========================================
// PREPARE FIRESTORE DOCUMENT
// ========================================
// Update FIREBASE_PROJECT_ID with your project ID
// ========================================

const data = $input.first().json;
const story = data.finalStory;

// ‚ö†Ô∏è REPLACE WITH YOUR PROJECT ID
const FIREBASE_PROJECT_ID = 'your-project-id';

function toFirestore(value) {
  if (value === null || value === undefined) return { nullValue: null };
  if (typeof value === 'boolean') return { booleanValue: value };
  if (typeof value === 'number') {
    return Number.isInteger(value) ? { integerValue: String(value) } : { doubleValue: value };
  }
  if (typeof value === 'string') return { stringValue: value };
  if (Array.isArray(value)) {
    return { arrayValue: { values: value.map(v => toFirestore(v)) } };
  }
  if (typeof value === 'object') {
    const fields = {};
    for (const [k, v] of Object.entries(value)) {
      fields[k] = toFirestore(v);
    }
    return { mapValue: { fields } };
  }
  return { stringValue: String(value) };
}

const docFields = {
  id: toFirestore(story.id),
  title: toFirestore(story.title),
  titleEnglish: toFirestore(story.titleEnglish),
  summary: toFirestore(story.summary),
  summaryEnglish: toFirestore(story.summaryEnglish),
  language: toFirestore(story.language),
  targetAudience: toFirestore(story.targetAudience),
  mood: toFirestore(story.mood),
  moral: toFirestore(story.moral),
  imageStyle: toFirestore(story.imageStyle),
  totalScenes: toFirestore(story.totalScenes),
  scenes: toFirestore(story.scenes),
  characters: toFirestore(story.characters),
  metadata: toFirestore(story.metadata),
  createdAt: { timestampValue: new Date().toISOString() }
};

return {
  json: {
    body: { fields: docFields },
    collection: `stories/${data.language}/items`,
    docId: story.id,
    projectId: FIREBASE_PROJECT_ID,
    finalStory: story
  }
};

