// ============================================
// PREPARE CARD DATA FOR MEDIA GENERATION
// ============================================

const response = $input.first().json;
const formData = $('Validate Input').first().json;

const deck = JSON.parse(response.choices[0].message.content);

function sanitize(text) {
  if (!text) return '';
  return text
    .replace(/\\/g, '\\\\')
    .replace(/"/g, "'")
    .replace(/\n/g, ' ')
    .replace(/[\x00-\x1F\x7F]/g, '')
    .trim();
}

const preparedCards = deck.cards.map((card, index) => {
  const dallePrompt = `${formData.imageStyle}, single object illustration representing "${sanitize(card.translation)}": ${sanitize(card.imagePrompt)}. Clean white background, centered, no text, simple and memorable, high quality.`;

  return {
    ...card,
    cardIndex: index,
    dallePrompt: dallePrompt.substring(0, 4000),
    imageBase64: null,
    wordAudioBase64: null,
    exampleAudioBase64: null
  };
});

return {
  json: {
    deck: { ...deck, cards: preparedCards },
    formData: formData,
    deckId: formData.deckId,
    email: formData.email,
    targetVoiceId: formData.targetVoiceId,
    nativeVoiceId: formData.nativeVoiceId,
    generateReverse: formData.generateReverse,
    totalCards: preparedCards.length,
    createdAt: formData.createdAt
  }
};

// Split cards into individual items for loop processing
const data = $input.first().json;
const cards = data.deck.cards;

return cards.map((card, index) => ({
  json: {
    card: card,
    cardIndex: index,
    totalCards: data.totalCards,
    formData: data.formData,
    deckId: data.deckId,
    email: data.email,
    targetVoiceId: data.targetVoiceId,
    nativeVoiceId: data.nativeVoiceId,
    generateReverse: data.generateReverse,
    deckName: data.deck.deckName,
    targetLanguage: data.deck.targetLanguage,
    nativeLanguage: data.deck.nativeLanguage,
    level: data.deck.level,
    createdAt: data.createdAt
  }
}));

// ============================================
// GENERATE IMAGE WITH DALL-E 3
// ============================================
// ‚ö†Ô∏è UPDATE THIS API KEY
const OPENAI_API_KEY = 'YOUR_OPENAI_API_KEY';
// ============================================

const data = $input.first().json;
const card = data.card;

try {
  const response = await fetch('https://api.openai.com/v1/images/generations', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${OPENAI_API_KEY}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      model: 'dall-e-3',
      prompt: card.dallePrompt,
      n: 1,
      size: '1024x1024',
      quality: 'standard',
      response_format: 'b64_json'
    })
  });

  const result = await response.json();

  if (result.error) {
    return {
      json: {
        ...data,
        card: {
          ...card,
          imageBase64: null,
          imageError: result.error.message
        }
      }
    };
  }

  return {
    json: {
      ...data,
      card: {
        ...card,
        imageBase64: result.data[0].b64_json,
        imageError: null
      }
    }
  };

} catch (error) {
  return {
    json: {
      ...data,
      card: {
        ...card,
        imageBase64: null,
        imageError: error.message
      }
    }
  };
}

// Convert binary audio to base64 and merge with card data
const inputData = $('Generate Image (DALL-E)').first().json;
const binaryData = $input.first().binary;

let wordAudioBase64 = null;
let wordAudioError = null;

try {
  if (binaryData && binaryData.data) {
    wordAudioBase64 = binaryData.data.data;
  }
} catch (error) {
  wordAudioError = error.message;
}

return {
  json: {
    ...inputData,
    card: {
      ...inputData.card,
      wordAudioBase64: wordAudioBase64,
      wordAudioError: wordAudioError
    }
  }
};

// Convert binary audio to base64 and merge with card data
const inputData = $('Convert Word Audio').first().json;
const binaryData = $input.first().binary;

let exampleAudioBase64 = null;
let exampleAudioError = null;

try {
  if (binaryData && binaryData.data) {
    exampleAudioBase64 = binaryData.data.data;
  }
} catch (error) {
  exampleAudioError = error.message;
}

return {
  json: {
    ...inputData,
    card: {
      ...inputData.card,
      exampleAudioBase64: exampleAudioBase64,
      exampleAudioError: exampleAudioError
    }
  }
};

// Pass through to next iteration or complete
const data = $input.first().json;
return { json: data };

// ============================================
// CREATE ANKI .APKG FILE
// ============================================

const allItems = $input.all();
const firstItem = allItems[0].json;

const processedCards = allItems.map(item => item.json.card);

const deckId = firstItem.deckId;
const deckName = firstItem.deckName;
