let mediaIndex = 0;

processedCards.forEach((card, cardIdx) => {
  if (card.imageBase64) {
    const imgName = `img_${cardIdx}.png`;
    mediaMapping[mediaIndex.toString()] = imgName;
    mediaFiles[mediaIndex.toString()] = {
      name: imgName,
      data: card.imageBase64,
      type: 'image'
    };
    card.imageFileName = imgName;
    mediaIndex++;
  }

  if (card.wordAudioBase64) {
    const audioName = `word_${cardIdx}.mp3`;
    mediaMapping[mediaIndex.toString()] = audioName;
    mediaFiles[mediaIndex.toString()] = {
      name: audioName,
      data: card.wordAudioBase64,
      type: 'audio'
    };
    card.wordAudioFileName = audioName;
    mediaIndex++;
  }

  if (card.exampleAudioBase64) {
    const audioName = `example_${cardIdx}.mp3`;
    mediaMapping[mediaIndex.toString()] = audioName;
    mediaFiles[mediaIndex.toString()] = {
      name: audioName,
      data: card.exampleAudioBase64,
      type: 'audio'
    };
    card.exampleAudioFileName = audioName;
    mediaIndex++;
  }
});

