  ]);
}

for (const card of apkg.cards) {
  db.run(`
    INSERT INTO cards VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
  `, [
    card.id,
    card.nid,
    card.did,
    card.ord,
    card.mod,
    card.usn,
    card.type,
    card.queue,
    card.due,
    card.ivl,
    card.factor,
    card.reps,
    card.lapses,
    card.left,
    card.odue,
    card.odid,
    card.flags,
    card.data
  ]);
}

const dbData = db.export();
const dbBuffer = Buffer.from(dbData);

const zip = new JSZip();

zip.file('collection.anki21', dbBuffer, { compression: 'DEFLATE' });
zip.file('media', JSON.stringify(apkg.mediaMapping));

for (const [index, mediaInfo] of Object.entries(apkg.mediaFiles)) {
  const buffer = Buffer.from(mediaInfo.data, 'base64');
  zip.file(index, buffer);
}

const zipBuffer = await zip.generateAsync({ 
  type: 'nodebuffer',
  compression: 'DEFLATE',
  compressionOptions: { level: 6 }
});

const apkgBase64 = zipBuffer.toString('base64');

const sheetRows = data.processedCards.map((card, idx) => ({
  word: card.word || '',
  reading: card.reading || '',
  romanization: card.romanization || '',
  translation: card.translation || '',
  part_of_speech: card.partOfSpeech || '',
  example: card.example || '',
  example_translation: card.exampleTranslation || '',
  has_image: card.imageBase64 ? 'Yes' : 'No',
  has_word_audio: card.wordAudioBase64 ? 'Yes' : 'No',
  has_example_audio: card.exampleAudioBase64 ? 'Yes' : 'No',
  notes: card.notes || '',
  level: data.level,
  target_language: data.targetLanguage,
  native_language: data.nativeLanguage,
  deck_name: data.deckName,
  created_at: data.createdAt
}));

return {
  json: {
    apkgBase64: apkgBase64,
    apkgFileName: `${data.deckName.replace(/[^a-zA-Z0-9]/g, '_')}.apkg`,
    email: data.email,
    deckName: data.deckName,
    targetLanguage: data.targetLanguage,
    nativeLanguage: data.nativeLanguage,
    level: data.level,
    totalCards: data.totalCards,
    generateReverse: data.generateReverse,
    sheetRows: sheetRows,
    createdAt: data.createdAt
  }
};

// Split rows for Google Sheets
const data = $input.first().json;
const rows = data.sheetRows;

return rows.map(row => ({
  json: {
    ...row,
    _apkgData: {
      apkgBase64: data.apkgBase64,
      apkgFileName: data.apkgFileName,
      email: data.email,
      deckName: data.deckName,
      targetLanguage: data.targetLanguage,
      nativeLanguage: data.nativeLanguage,
      level: data.level,
      totalCards: data.totalCards,
      generateReverse: data.generateReverse
    }
  }
}));

// Aggregate results after sheets save
const allItems = $input.all();
const firstItem = allItems[0].json._apkgData;

return {
  json: {
    ...firstItem,
    savedToSheets: allItems.length
  }
};

// Prepare binary data for email attachment
const data = $input.first().json;

const binaryData = {
  data: {
    data: data.apkgBase64,
    mimeType: 'application/octet-stream',
    fileName: data.apkgFileName
  }
};

return {
  json: data,
  binary: binaryData
};

