parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"riskScore": {
			"type": "number",
			"description": "Overall risk score from 0-100"
		},
		"riskLevel": {
			"type": "string",
			"description": "Risk category: low, moderate, elevated, or high"
		},
		"riskyBehaviors": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of identified risky driving patterns"
		},
		"reasoning": {
			"type": "string",
			"description": "Detailed explanation of the risk assessment"
		},
		"recommendations": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "Suggested actions or interventions"
		},
		"vehicleId": {
			"type": "string",
			"description": "Vehicle identifier from input data"
		},
		"driverId": {
			"type": "string",
			"description": "Driver identifier from input data"
		}
	}
}

parameters.text:
=:warning: *HIGH RISK ALERT*
*Driver:* {{ $json.driverId }}
*Vehicle:* {{ $json.vehicleId }}
*Risk Score:* {{ $json.riskScore }}/100 ({{ $json.riskLevel }})
*Premium Adjustment:* +{{ $json.premiumAdjustmentPercent }}%
*Top Risky Behaviors:* {{ $json.riskyBehaviors.slice(0, 3).join(', ') }}

