parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"overallSentiment": {
			"type": "string",
			"description": "Overall sentiment: positive, negative, or neutral"
		},
		"sentimentScore": {
			"type": "number",
			"description": "Sentiment score from -1 (very negative) to 1 (very positive)"
		},
		"totalMentions": {
			"type": "number",
			"description": "Total number of mentions analyzed"
		},
		"positiveMentions": {
			"type": "number",
			"description": "Count of positive mentions"
		},
		"negativeMentions": {
			"type": "number",
			"description": "Count of negative mentions"
		},
		"neutralMentions": {
			"type": "number",
			"description": "Count of neutral mentions"
		},
		"isCrisis": {
			"type": "boolean",
			"description": "Whether a potential PR crisis has been detected"
		},
		"crisisLevel": {
			"type": "string",
			"description": "Crisis severity: low, medium, high, critical"
		},
		"crisisReasons": {
			"type": "array",
			"description": "List of reasons for crisis detection",
			"items": {
				"type": "string"
			}
		},
		"emergingTrends": {
			"type": "array",
			"description": "List of emerging trends and topics",
			"items": {
				"type": "string"
			}
		},
		"keyInfluencers": {
			"type": "array",
			"description": "List of influential accounts or sources",
			"items": {
				"type": "string"
			}
		},
		"recommendations": {
			"type": "array",
			"description": "Actionable recommendations for teams",
			"items": {
				"type": "string"
			}
		},
		"topNegativeIssues": {
			"type": "array",
			"description": "Top negative issues mentioned",
			"items": {
				"type": "string"
			}
		}
	}
}

