parameters.jsCode:
/**
 * Forecast Engine (Deterministic) â€” Monthly Version
 *
 * INPUT (expects one item from Data Cleaning):
 * {
 * data: [
 * { Year: 2015, Month: 1, Sales: 138523, period: "2015-01" },
 * ...
 * ]
 * }
 *
 * OUTPUT (one item):
 * {
 * series: [{ period: "2015-01", Sales: ... }, ...],
 * candidates: [ ... ],
 * recommended: { ... forecastHorizon: "2020-04" ... }
 * }
 */

function num(x) {
  const n = typeof x === "number" ? x : parseFloat(String(x).replace(/,/g, "").trim());
  return Number.isFinite(n) ? n : NaN;
}

function buildPeriod(y, m) {
  return `${y}-${String(m).padStart(2, '0')}`;
}

function parsePeriod(p) {
  // Regex for YYYY-MM
  const m = /^(\d{4})-(\d{2})$/.exec(String(p).trim());
  if (!m) return null;
  return { year: parseInt(m[1], 10), month: parseInt(m[2], 10) };
}

function nextPeriodLabel(lastPeriod) {
  const parsed = parsePeriod(lastPeriod);
  if (!parsed) return "next_period";
  let { year, month } = parsed;
  month += 1;
  if (month === 13) {
    month = 1;
    year += 1;
  }
  return buildPeriod(year, month);
}

function rmse(actual, pred) {
  const errs = [];
  for (let i = 0; i < actual.length; i++) {
    const a = actual[i], p = pred[i];
    if (!Number.isFinite(a) || !Number.isFinite(p)) continue;
    errs.push((a - p) ** 2);
  }
  if (errs.length === 0) return null;
  return Math.sqrt(errs.reduce((s, e) => s + e, 0) / errs.length);
}

function mapePct(actual, pred) {
  const errs = [];
  for (let i = 0; i < actual.length; i++) {
    const a = actual[i], p = pred[i];
    if (!Number.isFinite(a) || !Number.isFinite(p)) continue;
    if (a === 0) continue; // skip zeros
    errs.push(Math.abs((a - p) / a));
  }
  if (errs.length === 0) return null;
  return (errs.reduce((s, e) => s + e, 0) / errs.length) * 100;
}

function formatPct(x, decimals = 1) {
  if (x === null || x === undefined || !Number.isFinite(x)) return null;
  return `${x.toFixed(decimals)}%`;
}

function dot(a, b) {
  let s = 0;
  for (let i = 0; i < a.length; i++) s += a[i] * b[i];
  return s;
}

// Linear regression y = a + b*t
function fitLinear(ts, ys) {
  const n = ts.length;
  if (n < 2) return null;

  const tMean = ts.reduce((s, v) => s + v, 0) / n;
  const yMean = ys.reduce((s, v) => s + v, 0) / n;

  let nume = 0;
  let deno = 0;
  for (let i = 0; i < n; i++) {
    const dt = ts[i] - tMean;
    nume += dt * (ys[i] - yMean);
    deno += dt * dt;
  }
  if (deno === 0) return null;

  const b = nume / deno;
  const a = yMean - b * tMean;
  return { a, b };
}

// Simple exponential smoothing (level-only)
function sesForecastOneStep(history, alpha) {
  if (history.length === 0) return null;
  let level = history[0];
  for (let i = 1; i < history.length; i++) {
    level = alpha * history[i] + (1 - alpha) * level;
  }
  return level;
}

// ----- OLS helpers (small-matrix Gauss-Jordan) -----
function solveLinearSystem(A, b) {
  const n = A.length;
  const M = A.map((row, i) => row.slice().concat([b[i]]));

  for (let col = 0; col < n; col++) {
    let pivotRow = col;
    for (let r = col + 1; r < n; r++) {
      if (Math.abs(M[r][col]) > Math.abs(M[pivotRow][col])) pivotRow = r;
    }
    if (Math.abs(M[pivotRow][col]) < 1e-12) return null;

    if (pivotRow !== col) {
      const tmp = M[col];
      M[col] = M[pivotRow];
      M[pivotRow] = tmp;
    }

    const pivot = M[col][col];
    for (let c = col; c <= n; c++) M[col][c] /= pivot;

    for (let r = 0; r < n; r++) {
      if (r === col) continue;
      const factor = M[r][col];
      for (let c = col; c <= n; c++) M[r][c] -= factor * M[col][c];
    }
  }

  return M.map((row) => row[n]);
}

function fitOLS(X, y, ridge = 1e-8) {
  const n = X.length;
  const k = X[0].length;

  const XtX = Array.from({ length: k }, () => Array(k).fill(0));
  const Xty = Array(k).fill(0);

  for (let i = 0; i < n; i++) {
    const xi = X[i];
    const yi = y[i];
    for (let a = 0; a < k; a++) {
      Xty[a] += xi[a] * yi;
      for (let b = 0; b < k; b++) {
        XtX[a][b] += xi[a] * xi[b];
      }
    }
  }

  for (let d = 0; d < k; d++) XtX[d][d] += ridge;

  return solveLinearSystem(XtX, Xty);
}

// Monthly Dummies: 11 dummies relative to January base
function monthDummies(m) {
  // m is 1..12
  // Jan (1) -> all zeros
  // Feb (2) -> index 0 is 1
  // ...
  // Dec (12) -> index 10 is 1
  const dummies = Array(11).fill(0);
  if (m > 1 && m <= 12) {
    dummies[m - 2] = 1;
  }
  return dummies;
}

// -------------------- MAIN --------------------
const input = items[0]?.json ?? {};
const raw = Array.isArray(input.data) ? input.data : [];

// Require at least 2 full years (24 months) for reliable seasonality
if (raw.length < 24) {
  // Warn but proceed if > 13, else throw
  if (raw.length < 13) {
    throw new Error("Not enough data. Provide at least 13 months for monthly seasonality (ideally 24+).");
  }
}

// Normalize + sort
const series = raw
  .map((r) => {
    const y = r.Year ?? r.year;
    const m = r.Month ?? r.month; // Read Month
    const s = num(r.Sales ?? r.sales);
    const period = r.period ? String(r.period) : buildPeriod(y, m);
    const parsed = parsePeriod(period);
    return {
      period,
      year: parsed?.year ?? y,
      month: parsed?.month ?? m,
      Sales: s,
    };
  })
  .filter((r) => Number.isFinite(r.year) && Number.isFinite(r.month) && Number.isFinite(r.Sales))
  .sort((a, b) => (a.year - b.year) || (a.month - b.month));

const y = series.map((r) => r.Sales);
const periods = series.map((r) => r.period);

const seasonLag = 12;             // Monthly seasonality
const maWindow = 3;               // Short-term moving average (3 months)
const alphas = [0.2, 0.4, 0.6, 0.8];
const minBacktestPoints = 6;

// Rolling one-step backtest: train on [0..t-1], predict y[t]
function backtest(methodName) {
  const actual = [];
  const pred = [];

  for (let t = 0; t < y.length; t++) {
    const hist = y.slice(0, t);
    let yhat = null;

    if (methodName === "seasonal_naive") {
      if (t - seasonLag >= 0) yhat = y[t - seasonLag];
    }

    if (methodName === "seasonal_average") {
      const m = series[t].month;
      const vals = series
        .slice(0, t)
        .filter((r) => r.month === m) // Filter by same month
        .map((r) => r.Sales);
      if (vals.length >= 1) yhat = vals.reduce((s, v) => s + v, 0) / vals.length;
    }

    if (methodName === "moving_average") {
      if (hist.length >= 1) {
        const window = hist.slice(-Math.min(maWindow, hist.length));
        yhat = window.reduce((s, v) => s + v, 0) / window.length;
      }
    }

    if (methodName === "trend_linear") {
      if (hist.length >= 3) {
        const ts = hist.map((_, i) => i);
        const fit = fitLinear(ts, hist);
        if (fit) {
          const nextT = hist.length;
          yhat = fit.a + fit.b * nextT;
        }
      }
    }

    if (methodName === "exp_smoothing") {
      if (hist.length >= 2) {
        let bestAlpha = null;
        let bestErr = Infinity;

        for (const a of alphas) {
          const aAct = [];
          const aPred = [];
          for (let i = 1; i < hist.length; i++) {
            const h = hist.slice(0, i);
            const f = sesForecastOneStep(h, a);
            if (Number.isFinite(f)) {
              aAct.push(hist[i]);
              aPred.push(f);
            }
          }
          const e = rmse(aAct, aPred);
          if (e !== null && e < bestErr) {
            bestErr = e;
            bestAlpha = a;
          }
        }

        if (bestAlpha !== null) {
          yhat = sesForecastOneStep(hist, bestAlpha);
        }
      }
    }

    if (methodName === "seasonal_regression") {
      // intercept + 11 month dummies = 12 params. Need > 13 points.
      if (hist.length >= 15) {
        const X = [];
        const Y = [];
        for (let i = 0; i < hist.length; i++) {
          const d = monthDummies(series[i].month);
          X.push([1, ...d]);
          Y.push(hist[i]);
        }
        const beta = fitOLS(X, Y);
        if (beta) {
          const dT = monthDummies(series[t].month);
          yhat = dot(beta, [1, ...dT]);
        }
      }
    }

    if (methodName === "seasonal_regression_trend") {
      // intercept + time + 11 dummies = 13 params. Need > 14 points.
      if (hist.length >= 16) {
        const X = [];
        const Y = [];
        for (let i = 0; i < hist.length; i++) {
          const d = monthDummies(series[i].month);
          X.push([1, i, ...d]);
          Y.push(hist[i]);
        }
        const beta = fitOLS(X, Y);
        if (beta) {
          const dT = monthDummies(series[t].month);
          yhat = dot(beta, [1, hist.length, ...dT]);
        }
      }
    }

    if (Number.isFinite(yhat)) {
      actual.push(y[t]);
      pred.push(yhat);
    }
  }

  if (actual.length < minBacktestPoints) return null;

  const r = rmse(actual, pred);
  const m = mapePct(actual, pred);

  return {
    method: methodName,
    backtest_points: actual.length,
    rmse: r,
    mape: m,
    mapeFormatted: formatPct(m, 1),
  };
}

const methods = [
  "seasonal_naive",
  "seasonal_average",
  "moving_average",
  "trend_linear",
  "exp_smoothing",
  "seasonal_regression",
  "seasonal_regression_trend",
];

const candidates = methods.map((m) => backtest(m)).filter(Boolean);

if (candidates.length === 0) {
  throw new Error("No method had enough backtest points. Need more historical monthly data.");
}

// Choose best: lowest MAPE, tie-breaker lowest RMSE
candidates.sort((a, b) => {
  const aM = a.mape, bM = b.mape;
  const aR = a.rmse, bR = b.rmse;

  if (Number.isFinite(aM) && Number.isFinite(bM)) {
    if (aM !== bM) return aM - bM;
    return (aR ?? Infinity) - (bR ?? Infinity);
  }
  if (Number.isFinite(aM) && !Number.isFinite(bM)) return -1;
  if (!Number.isFinite(aM) && Number.isFinite(bM)) return 1;
  return (aR ?? Infinity) - (bR ?? Infinity);
});

const best = candidates[0];

// Forecast next month using best method on full history
function forecastNext(methodName) {
  const n = y.length;

  if (methodName === "seasonal_naive") {
    return n - seasonLag >= 0 ? y[n - seasonLag] : null;
  }

  if (methodName === "seasonal_average") {
    const lastM = series[n - 1].month;
    let nextM = lastM + 1;
    if (nextM === 13) nextM = 1;

    const vals = series.filter((r) => r.month === nextM).map((r) => r.Sales);
    return vals.length ? vals.reduce((s, v) => s + v, 0) / vals.length : null;
  }

  if (methodName === "moving_average") {
    const window = y.slice(-Math.min(maWindow, n));
    return window.reduce((s, v) => s + v, 0) / window.length;
  }

  if (methodName === "trend_linear") {
    const ts = y.map((_, i) => i);
    const fit = fitLinear(ts, y);
    if (!fit) return null;
    return fit.a + fit.b * n;
  }

  if (methodName === "exp_smoothing") {
    let bestAlpha = null;
    let bestErr = Infinity;

    for (const a of alphas) {
      const aAct = [];
      const aPred = [];
      for (let i = 1; i < y.length; i++) {
        const h = y.slice(0, i);
        const f = sesForecastOneStep(h, a);
        if (Number.isFinite(f)) {
          aAct.push(y[i]);
          aPred.push(f);
        }
      }
      const e = rmse(aAct, aPred);
      if (e !== null && e < bestErr) {
        bestErr = e;
        bestAlpha = a;
      }
    }

    return bestAlpha === null ? null : sesForecastOneStep(y, bestAlpha);
  }

  if (methodName === "seasonal_regression") {
    if (y.length < 15) return null;

    const X = [];
    const Y = [];
    for (let i = 0; i < y.length; i++) {
      const d = monthDummies(series[i].month);
      X.push([1, ...d]);
      Y.push(y[i]);
    }
    const beta = fitOLS(X, Y);
    if (!beta) return null;

    let nextM = series[series.length - 1].month + 1;
    if (nextM === 13) nextM = 1;
    const dN = monthDummies(nextM);
    return dot(beta, [1, ...dN]);
  }

  if (methodName === "seasonal_regression_trend") {
    if (y.length < 16) return null;

    const X = [];
    const Y = [];
    for (let i = 0; i < y.length; i++) {
      const d = monthDummies(series[i].month);
      X.push([1, i, ...d]);
      Y.push(y[i]);
    }
    const beta = fitOLS(X, Y);
    if (!beta) return null;

    let nextM = series[series.length - 1].month + 1;
    if (nextM === 13) nextM = 1;
    const dN = monthDummies(nextM);

    const nextIndex = y.length;
    return dot(beta, [1, nextIndex, ...dN]);
  }

  return null;
}

const forecastedSales = forecastNext(best.method);
if (!Number.isFinite(forecastedSales)) {
  throw new Error(`Best method (${best.method}) could not produce a next-month forecast.`);
}

const forecastHorizon = nextPeriodLabel(periods[periods.length - 1]);

// ---- Presentation rounding ----
const forecastedSalesRounded = Math.round(forecastedSales);
const rmseRounded = Number.isFinite(best.rmse) ? Math.round(best.rmse) : best.rmse;
const mapeRounded = Number.isFinite(best.mape) ? Number(best.mape.toFixed(1)) : best.mape;
const mapeFormattedRounded = Number.isFinite(mapeRounded) ? `${mapeRounded}%` : best.mapeFormatted;

return [
  {
    json: {
      series: series.map((r) => ({ period: r.period, Sales: r.Sales })),
      candidates,
      recommended: {
        forecastHorizon,
        forecastedSales: forecastedSalesRounded,
        forecastingTechnique: best.method,
        rmse: rmseRounded,
        mape: mapeRounded,
        mapeFormatted: mapeFormattedRounded,
        backtestPoints: best.backtest_points,
      },
      selectionRule: "lowest MAPE; tie-breaker RMSE",
    },
  },
];

// n8n Code node: Run Once for All Items

function toNumber(v) {
  const n = typeof v === 'number' ? v : parseFloat(String(v).trim());
  return Number.isFinite(n) ? n : NaN;
}

const rows = items
  .map(i => i.json)
  .filter(r =>
    Number.isFinite(toNumber(r.Year)) &&
    Number.isFinite(toNumber(r.Month)) &&         // Changed: Check for Month
    Number.isFinite(toNumber(r.MonthlySales))     // Changed: Check for MonthlySales
  )
  .map(r => ({
    ...r,
    Year: toNumber(r.Year),
    Month: toNumber(r.Month),                     // Changed: Store Month
    Sales: toNumber(r.MonthlySales),              // Changed: Map input 'MonthlySales' to generic 'Sales' for downstream compatibility
    period: `${toNumber(r.Year)}-${String(toNumber(r.Month)).padStart(2, '0')}` // Changed: Format period as YYYY-MM (e.g., 2015-01)
  }))
  .sort((a, b) => (a.Year - b.Year) || (a.Month - b.Month)); // Changed: Sort by Month

// Output ONE item that contains the FULL dataset as an array:
return [
  {
    json: {
      data: rows,
      rowCount: rows.length
    }
  }
];

