parameters.jsCode:
const row = $json;

let history = [];
try {
  history = JSON.parse(row.historySales || "[]");
} catch (e) {
  history = [];
}

// SAFETY STEP: Normalize keys to ensure lowercase 'year', 'month', 'sales'
// This prevents errors if the database accidentally has "Year" or "MonthlySales"
const historyNormalized = history.map(item => ({
  year: Number(item.year || item.Year),
  month: Number(item.month || item.Month), 
  sales: Number(item.sales || item.Sales || item.MonthlySales)
}));

return [
  {
    json: {
      ...row,
      historySalesParsed: historyNormalized,
    },
  },
];

const data = items[0].json.data;
const forecast = items[0].json.output;

if (!Array.isArray(data) || data.length === 0) {
  throw new Error("json.data is missing or empty.");
}

const labels = data.map(d => d.period);
const actual = data.map(d => Number(d.Sales));

// --- CHANGE START ---
// Get the very last historical sales value
const lastActualValue = actual[actual.length - 1];
const forecastValue = Number(forecast.forecastedSales);

// Create forecast series:
// 1. Fill with 'null' for all periods except the last two.
// 2. Place the last historical value at the second-to-last position.
// 3. Place the forecast value at the very last position.
const forecastSeries = Array(actual.length - 1).fill(null);
forecastSeries.push(lastActualValue);
forecastSeries.push(forecastValue);
// --- CHANGE END ---

const nextPeriod = forecast?.forecastHorizon || "Next";
const labelsWithForecast = labels.concat([nextPeriod]);

// The actual series still needs to end with 'null' so the blue line stops.
const actualWithNull = actual.concat([null]);

const chartConfig = {
  type: "line",
  data: {
    labels: labelsWithForecast,
    datasets: [
      {
        label: "Historical Sales",
        data: actualWithNull,
        fill: false,
        tension: 0.2,
        borderColor: "rgb(54, 162, 235)",
        backgroundColor: "rgba(54, 162, 235, 0.5)",
        pointRadius: 3 // Standard size for historical points
      },
      {
        label: "Forecast (Next Month)",
        data: forecastSeries,
        fill: false,
        tension: 0.2,
        borderDash: [6, 6], // The connecting line will be dashed
        borderColor: "rgb(255, 99, 132)", // Pink line color
        backgroundColor: "rgba(255, 99, 132, 0.5)",
        pointRadius: 5, // Make the forecast dot larger
        pointHoverRadius: 7
      }
    ]
  },
  options: {
    responsive: true,
    plugins: {
      title: { display: true, text: "Monthly Sales (Historical + Forecast)" },
      legend: { display: true },
      // Optional: Add a tooltip callback to label the forecast clearly
      tooltip: {
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
                label += ': ';
            }
            if (context.parsed.y !== null) {
                label += new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD', maximumFractionDigits: 0 }).format(context.parsed.y);
            }
            return label;
          }
        }
      }
    },
    scales: {
      y: {
        title: { display: true, text: "Sales" },
        beginAtZero: false
      },
      x: {
        title: { display: true, text: "Month" },
        ticks: {
          autoSkip: true,
          maxTicksLimit: 20
        }
      }
    }
  }
};

return [{ json: { ...items[0].json, chartConfig } }];
