const toVNTime = (d = new Date()) => {
  const date = new Date(d.getTime() + 7 * 60 * 60 * 1000); // UTC+7
  return date;
};

const formatDate = (date) => {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, "0");
  const d = String(date.getDate()).padStart(2, "0");
  return `${y}-${m}-${d}`;
};

const getDayOfWeek = (date) => {
  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  return days[date.getDay()];
};

const titleSheet = (date) => {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, "0");
  return `${y}-${m}`;
};

const now = toVNTime();
const yesterday = new Date(now);
yesterday.setDate(yesterday.getDate() - 1);

const twoDaysAgo = new Date(now);
twoDaysAgo.setDate(twoDaysAgo.getDate() - 2);

const tomorrow = new Date(now);
tomorrow.setDate(tomorrow.getDate() + 1);

const todayString = formatDate(now);
const tomorrowString = formatDate(tomorrow);

return [{
  today: todayString,
  yesterday: formatDate(yesterday),
  twoDaysAgo: formatDate(twoDaysAgo),
  tomorrow: tomorrowString,
  dayOfWeekYesterday: getDayOfWeek(yesterday),
  monthYesterday: titleSheet(yesterday),
}];

