const inputData = $input.all();
if (!inputData?.length || !inputData[0]?.json?.result?.length) {
  throw new Error("No leave record data found in input");
}

const record = inputData[0].json.result[0];
const display_name_leave_record = record.display_name;
const rawFrom = record.date_from;
const rawTo = record.date_to;
if (!rawFrom || !rawTo) {
  throw new Error("Missing date_from or date_to in leave record");
}

function parseAndAdjustDate(dateStr, offsetHours = 7) {
  const normalized = dateStr.includes("T") ? dateStr : dateStr.replace(" ", "T");
  const dt = new Date(normalized);

  if (isNaN(dt.getTime())) {
    throw new Error(`Invalid date format: ${dateStr}`);
  }

  dt.setHours(dt.getHours() + offsetHours);
  return dt;
}

function formatDate(dt) {
  const pad = (n) => String(n).padStart(2, "0");
  return `${dt.getFullYear()}-${pad(dt.getMonth() + 1)}-${pad(dt.getDate())} ` +
         `${pad(dt.getHours())}:${pad(dt.getMinutes())}:${pad(dt.getSeconds())}`;
}

const dateFromObj = parseAndAdjustDate(rawFrom);
const dateToObj = parseAndAdjustDate(rawTo);

return {
  display_name_leave_record,
  date_from: formatDate(dateFromObj),
  date_to: formatDate(dateToObj)
};

