const inputData = $input.all();
const projects = $node["Step5: Get a list of closed projects."].json.projects;

const excludedprojectIds = new Set([
  8, 9, 10, 12,
  ...projects.map(p => p.id)
]);

const membershipsProject = inputData[0].json.user.memberships;
const redmine_userID = inputData[0].json.user.id;
const redmine_mail = inputData[0].json.user.mail;

const result = membershipsProject
  .filter(record => !excludedprojectIds.has(record.project.id))
  .map(record => ({ id: record.project.id }));

return [
