];

const redmine_projectTeamMemberIds = $input.first().json.redmine_projectTeamMemberIds;
const usersData = $node["Step4: Get all user in Redmine6"].json.users;

const redmine_projectTeamMemberInfo = redmine_projectTeamMemberIds.map(member => {
  const found = usersData.find(user => user.id === member.id);

  return {
    redmine_userID: member.id,
    redmine_mail: found ? found.mail : null
  };
});

return {redmine_projectTeamMemberInfo};

const inputData = $input.all();
const redmineMembers = inputData[0].json.redmine_projectTeamMemberInfo || [];
const mergedResults = $items("Step6: Get many users");
const display_name_leave_record = $input.first().json.display_name_leave_record;
const date_from_leave_record = $input.first().json.date_from;
const date_to_leave_record = $input.first().json.date_to;
const odoo_leave_record_id = $input.first().json.odoo_leave_record_id;
const time_off_approval_link = $node["Step2: Set Variables"].json.time_off_approval_link;
const odoo_manager_work_email = $input.first().json.odoo_manager_work_email;

function findMemberIDByEmail(email) {
  if (!email) return null;

  const cleanEmail = String(email).toLowerCase().trim();
  const local = cleanEmail.split("@")[0];

  let bestMatch = null;
  let bestScore = -1;

  for (const item of mergedResults) {
    const data = item.json || {};
    if (!data.Email) continue;

    const itemEmail = String(data.Email).toLowerCase().trim();
    const itemLocal = itemEmail.split("@")[0];

    let score = 0;
    if (itemEmail === cleanEmail) score = 100;
    else if (itemLocal === local) score = 80;
    else if (itemEmail.includes(local)) score = 50;

    if (score > bestScore) {
      bestScore = score;
      bestMatch = data;
    }
  }

  return bestMatch ? bestMatch.memberID : null;
}

// -------------------------------------------------
// 1) If redmineMembers is empty â†’ only process management emails
// -------------------------------------------------
if (redmineMembers.length === 0) {
  const managerMemberID = findMemberIDByEmail(odoo_manager_work_email);

  return [
    {
      type: "timeoff",
      memberID: managerMemberID || undefined,
      title: display_name_leave_record,
      date_from: date_from_leave_record,
      date_to: date_to_leave_record,
      link: time_off_approval_link + odoo_leave_record_id,
      isManager: true,
      redmine_mail: odoo_manager_work_email
    }
  ];
}

// -------------------------------------------------
// 2) If redmineMembers is not empty
// -------------------------------------------------
for (const member of redmineMembers) {
  const inputEmail = String(member.redmine_mail || "").toLowerCase().trim();
  const inputLocal = inputEmail.split("@")[0];

  let bestMatch = null;
  let bestScore = -1;

  for (const item of mergedResults) {
    const data = item.json || {};
    if (!data.Email) continue;

    const itemEmail = String(data.Email).toLowerCase().trim();
    const itemLocal = itemEmail.split("@")[0];

    let score = 0;

    if (itemEmail === inputEmail) score = 100;
    else if (itemLocal === inputLocal) score = 80;
    else if (itemEmail.includes(inputLocal)) score = 50;

    if (score > bestScore) {
      bestScore = score;
      bestMatch = data;
    }
  }

  member.type = "timeoff";
  member.memberID = bestMatch ? bestMatch.memberID : undefined;
  member.title = display_name_leave_record;
  member.date_from = date_from_leave_record;
  member.date_to = date_to_leave_record;
  member.link = time_off_approval_link + odoo_leave_record_id;
}

// -------------------------------------------------
// 3) Find the managerMemberID and check if it's already in redmineMembers.
// -------------------------------------------------
const managerMemberID = findMemberIDByEmail(odoo_manager_work_email);

if (managerMemberID) {
  const exists = redmineMembers.some(m => m.memberID === managerMemberID);

  if (!exists) {
    redmineMembers.push({
      type: "timeoff",
      memberID: managerMemberID,
      title: display_name_leave_record,
      date_from: date_from_leave_record,
      date_to: date_to_leave_record,
      link: time_off_approval_link + odoo_leave_record_id,
      isManager: true,
      redmine_mail: odoo_manager_work_email
    });
  }
}

return redmineMembers;

const mergedResults = $items("Step6: Get many users");
const display_name_leave_record = $input.first().json.display_name_leave_record;
const date_from_leave_record = $input.first().json.date_from;
const date_to_leave_record = $input.first().json.date_to;
const odoo_leave_record_id = $input.first().json.odoo_leave_record_id;
const time_off_approval_link = $node["Step2: Set Variables"].json.time_off_approval_link;
const odoo_manager_work_email = $input.first().json.odoo_manager_work_email;

function findMemberIDByEmail(email) {
  if (!email) return null;

  const cleanEmail = String(email).toLowerCase().trim();
  const local = cleanEmail.split("@")[0];

  let bestMatch = null;
  let bestScore = -1;

  for (const item of mergedResults) {
    const data = item.json || {};
    if (!data.Email) continue;

    const itemEmail = String(data.Email).toLowerCase().trim();
    const itemLocal = itemEmail.split("@")[0];

    let score = 0;
    if (itemEmail === cleanEmail) score = 100;
    else if (itemLocal === local) score = 80;
    else if (itemEmail.includes(local)) score = 50;

    if (score > bestScore) {
      bestScore = score;
      bestMatch = data;
    }
  }

  return bestMatch ? bestMatch.memberID : null;
}

const managerMemberID = findMemberIDByEmail(odoo_manager_work_email);
return [
  {
    type: "timeoff",
    memberID: managerMemberID || undefined,
    title: display_name_leave_record,
    date_from: date_from_leave_record,
    date_to: date_to_leave_record,
    link: time_off_approval_link + odoo_leave_record_id,
    redmine_mail: odoo_manager_work_email
  }
];

