// Format for Telegram - NO TRUNCATION (Let Telegram handle it)
const input = $input.first().json;

const responses = input.responses;
const query = input.query;
const chatId = input.chatId;
const timestamp = input.timestamp;

// Telegram's ACTUAL limit is 4096, but we'll be safer
const MAX_MESSAGE_LENGTH = 4000;

// Clean markdown for Telegram HTML
function cleanMarkdown(text) {
  if (!text) return '';

  // Remove markdown formatting that Telegram HTML doesn't support
  text = text.replace(/\*\*\*(.+?)\*\*\*/g, '<b>$1</b>'); // ***bold italic*** ‚Üí bold
  text = text.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');     // **bold** ‚Üí <b>bold</b>
  text = text.replace(/\*(.+?)\*/g, '<i>$1</i>');         // *italic* ‚Üí <i>italic</i>
  text = text.replace(/###\s+(.+?)$/gm, '<b>$1</b>');     // ### Header ‚Üí <b>Header</b>
  text = text.replace(/##\s+(.+?)$/gm, '<b>$1</b>');      // ## Header ‚Üí <b>Header</b>
  text = text.replace(/#\s+(.+?)$/gm, '<b>$1</b>');       // # Header ‚Üí <b>Header</b>
  text = text.replace(/\n-\s+/g, '\n‚Ä¢ ');                 // - item ‚Üí ‚Ä¢ item

  return text;
}

// Escape HTML special characters
function escapeHtml(text) {
  if (!text) return '';
  return text.toString()
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');
}

// Smart truncate - cut at last complete sentence
function smartTruncate(text, maxLength) {
  if (!text || text.length <= maxLength) return text;

  // Try to cut at last period before limit
  const cutPoint = text.lastIndexOf('.', maxLength);
  if (cutPoint > maxLength - 200) { // Only if reasonably close
    return text.substring(0, cutPoint + 1) + '\n\n(Message truncated due to length)';
  }

  // Otherwise cut at last space
  const spacePoint = text.lastIndexOf(' ', maxLength);
  return text.substring(0, spacePoint) + '...\n\n(Truncated)';
}

const escapedQuery = escapeHtml(query);

// Create header message
const headerText = `üìä <b>Knowledge Ladder Results</b>\n\n` +
  `‚ùì <b>Question:</b> ${escapedQuery}\n` +
  `üìÖ <b>Date:</b> ${new Date(timestamp).toLocaleString()}\n\n` +
  `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
  `Sending 5 responses...`;

const outputs = [];

// Header
outputs.push({
  json: {
    chatId: chatId,
    text: headerText,
    messageNumber: 1
  }
});

// 5 Responses with markdown cleaning
responses.forEach((r, index) => {
  const title = r.title || 'Unknown';
  const emoji = r.emoji || '‚ùì';
  let content = r.content || 'No content';

  // Clean markdown FIRST
  content = cleanMarkdown(content);

  // Then smart truncate if needed
  const availableLength = MAX_MESSAGE_LENGTH - (title.length + emoji.length + 20);
  content = smartTruncate(content, availableLength);

  // Escape HTML (AFTER markdown cleaning)
  const escapedContent = escapeHtml(content);
  const escapedTitle = escapeHtml(title);

  const messageText = `${emoji} <b>${escapedTitle}</b>\n\n${escapedContent}`;

  console.log(`Message ${index + 2}: ${messageText.length} chars`);

  outputs.push({
    json: {
      chatId: chatId,
      text: messageText,
      messageNumber: index + 2
    }
  });
});

console.log(`‚úÖ Created ${outputs.length} messages`);

return outputs;

