// Calculate aggregate rankings
const stage2Results = $input.all();
const labeledResponses = $('Stage 2: Create Anonymized Responses').item.json.labeledResponses;

// Create label to model mapping
const labelToModel = {};
labeledResponses.forEach(item => {
  labelToModel[`Response ${item.label}`] = item.model;
});

// Track positions for each model
const modelPositions = {};

stage2Results.forEach(ranking => {
  // Access the data array first
  const dataArray = ranking.json.data;

  // Iterate through each ranking in the data array
  dataArray.forEach(rankingData => {
    const parsedRanking = rankingData.parsedRanking;

    parsedRanking.forEach((label, index) => {
      const position = index + 1;
      const modelName = labelToModel[label];

      if (modelName) {
        if (!modelPositions[modelName]) {
          modelPositions[modelName] = [];
        }
        modelPositions[modelName].push(position);
      }
    });
  });
});

// Calculate average rankings
const aggregateRankings = Object.entries(modelPositions).map(([model, positions]) => {
  const avgRank = positions.reduce((a, b) => a + b, 0) / positions.length;
  return {
    model: model,
    averageRank: Math.round(avgRank * 100) / 100,
    rankingsCount: positions.length
  };
});

// Sort by average rank (lower is better)
aggregateRankings.sort((a, b) => a.averageRank - b.averageRank);

return {
  json: {
    aggregateRankings: aggregateRankings,
    labelToModel: labelToModel,
    stage2Results: stage2Results.map(r => r.json)
  }
};
