const results = [];
for (const item of $input.all()) {
  const json = item.json;
  let result = { ...item };

  if (!json.file_name) {
    result.json.error = "File name is missing in the input data.";
    result.json.fileType = null;
  } else {
    const fileExtension = json.file_name.split('.').pop().toLowerCase();
    result.json.fileType = fileExtension === 'pdf'
      ? 'pdf'
      : fileExtension === 'docx'
      ? 'docx'
      : null;
  }
  results.push(result);
}
return results;
