const items = $input.all();

for (const item of items) {
  // Explicitly fetch data from Telegram Trigger node
  const telegramData = $node["Telegram Trigger"].json["message"] || {};
  const photoArray = telegramData.photo || [];

  console.log('Telegram Photo Array:', photoArray);

  if (photoArray.length > 0) {
    // Find the photo with the max file_size using reduce
    const maxPhoto = photoArray.reduce((max, curr) => 
      (curr.file_size > max.file_size ? curr : max), 
      photoArray[0]
    );
    console.log('Max Photo:', maxPhoto);

    item.json.selected_file_id = maxPhoto.file_id;
  } else {
    // Set to null if no photos are found
    item.json.selected_file_id = null;
    console.log('No photo versions found in Telegram message');
    // Optionally throw an error to enforce photo presence
    // throw new Error('No photo versions found in Telegram message');
  }
}

return items;

