const urlArray = imageUrlsString.split(',');

// Use the .reduce() method to transform the array into a single object
const finalObject = urlArray.reduce((accumulator, url, index) => {
  // Create a dynamic key like "image_url1", "image_url2", etc.
  // The 'index' starts at 0, so we add 1 to start our count from 1.
  const key = `image_url${index + 1}`;

  // Assign the trimmed URL to the new key on our object
  accumulator[key] = url.trim();

  // Return the object for the next iteration
  return accumulator;
}, {}); // The {} here initializes the process with an empty object

// Return the final object as a single n8n item
return [{
  json: finalObject
}];

