// Use a single .reduce() loop for better performance and clarity
const aggregatedData = $input.all().reduce((accumulator, item) => {
  const fileId = item.json.file_id;
  const imageUrl = item.json.image_url;

  // Only add the data if both properties exist on the item
  if (fileId && imageUrl) {
    accumulator.fileIdList.push(fileId);
    accumulator.imageUrlList.push(imageUrl);
  }

  // Return the accumulator for the next iteration
  return accumulator;
}, { fileIdList: [], imageUrlList: [] }); // This is the starting value: an object with two empty arrays

// Join the final arrays into comma-separated strings
const fileIds = aggregatedData.fileIdList.join(',');
const imageUrls = aggregatedData.imageUrlList.join(',');

// Return as a single item for the next nodes to use
return [{ 
  json: { 
    file_ids: fileIds, 
    image_urls: imageUrls 
  } 
}];

