items.forEach(item => {
  if (item.json.image_urls) {
    item.json.image_urls.split(',').forEach(url => {
      const trimmedUrl = url.trim();
      if (trimmedUrl) {
        // Remove trailing " if present
        const cleanUrl = trimmedUrl.replace(/\"$/, '');
        results.push({ json: { image_url: cleanUrl } });
      }
    });
  }
});

// Return individual items
return results;

