// Simple AI output parser - just extract the AI analysis

const allItems = $input.all();

return allItems.map((currentItem, index) => {
  let lossCategory = "";
  let confidenceLevel = "";
  let briefExplanation = "";

  try {
    const aiText = currentItem.json?.output?.[0]?.content?.[0]?.text;
    if (aiText) {
      const parsed = JSON.parse(aiText);
      lossCategory = parsed.lossCategory || "";
      confidenceLevel = parsed.confidenceLevel || "";
      briefExplanation = parsed.briefExplanation || "";
    }
  } catch (error) {
    console.log("AI parse error:", error);
    lossCategory = "Other";
    confidenceLevel = "Low";
    briefExplanation = "Could not parse AI response";
  }

  return {
    json: {
      itemIndex: index,  // Store the index to match later
      lossCategory,
      confidenceLevel,
      briefExplanation
    }
  };
});

