const allItems = $input.all();

return allItems.map((item, index) => {
  // üîπ Step 1: Get previous deal data from "Code in JavaScript" node

  // üîπ Step 2: Parse AI output (revival strategy)
  let reengagementTiming = "";
  let recommendedChannel = "";
  let messageAngle = "";
  let suggestedIncentive = "";
  let salesActionStep = "";

  try {
    // Access the AI response - note the structure from your response
    const aiOutput = item.json?.output?.[0]?.content?.[0];

    // Get text from either 'text' or 'output_text' field
    const aiText = aiOutput?.text || aiOutput?.output_text;

    if (aiText) {
      const parsed = JSON.parse(aiText);
      reengagementTiming = parsed.reengagementTiming || "";
      recommendedChannel = parsed.recommendedChannel || "";
      messageAngle = parsed.messageAngle || "";
      suggestedIncentive = parsed.suggestedIncentive || "";
      salesActionStep = parsed.salesActionStep || "";
    }
  } catch (e) {
    console.log("AI parse error:", e);
    // Fallbacks will be applied below
  }

  // üîπ Step 3: Fallbacks (never leave empty)
  if (!reengagementTiming) reengagementTiming = "30‚Äì60 days after loss";
  if (!recommendedChannel) recommendedChannel = "Personalized Email";
  if (!messageAngle) messageAngle = `Re-engage focusing on value related to ${lossCategory || "their needs"}.`;
  if (!suggestedIncentive) suggestedIncentive = "Educational content or limited trial";
  if (!salesActionStep) salesActionStep = "Send a personalized follow-up and track engagement.";

  // üîπ Step 4: Return MERGED object
  return {
    json: {

      reengagementTiming,
      recommendedChannel,
      messageAngle,
      suggestedIncentive,
      salesActionStep
    }
  };
});

