// This node filters the raw GitHub events for security risks
const items = $input.all();

return items.filter(item => {
  // Define what you consider a security event
  // you may leave out PushEvents e.g. commits, other less critical non-sec changes
  const criticalEvents = [
    'MemberEvent', // Someone added/removed from repo
    'PublicEvent', // Repo made public (VERY IMPORTANT)
    'TeamAddEvent', // Team access changed
    'PushEvent' // To See every commit event
  ];
  return criticalEvents.includes(item.json.type);
});
