// Filter messages from today only
const allMessages = $input.all();
const now = new Date();
const todayStart = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 0, 0, 0);
const todayEnd = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 23, 59, 59);

// Filter to today's messages
const todayMessages = allMessages.filter(item => {
  const msgDate = new Date(item.json.timestamp);
  return msgDate >= todayStart && msgDate <= todayEnd;
});

// Format the messages
const formattedMessages = todayMessages.map(item => ({
  id: item.json.id,
  content: item.json.content || '',
  author: item.json.author?.username || 'Unknown',
  timestamp: item.json.timestamp,
  attachments: item.json.attachments?.length || 0
})).reverse(); // Chronological order

return {
  json: {
    messages: formattedMessages,
    messageCount: formattedMessages.length,
    date: now.toISOString().split('T')[0],
    fetchedAt: now.toISOString()
  }
};

// Format messages for GPT-4 analysis
const data = $input.first().json;
const messages = data.messages;

if (!messages || messages.length === 0) {
  return { json: { error: 'No messages to process' } };
}

// Build conversation text
const conversationText = messages.map(msg => {
  const time = new Date(msg.timestamp).toLocaleTimeString('en-US', {
    hour: '2-digit',
    minute: '2-digit'
  });
  return `[${time}] ${msg.author}: ${msg.content}`;
}).join('\n');

// Extract unique participants
const participants = [...new Set(messages.map(m => m.author))];

// Count messages per person
const messageCounts = {};
messages.forEach(msg => {
  messageCounts[msg.author] = (messageCounts[msg.author] || 0) + 1;
});

return {
  json: {
    conversationText: conversationText,
    participants: participants,
    messageCounts: messageCounts,
    totalMessages: data.messageCount,
    date: data.date,
    dateFormatted: new Date(data.date).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    })
  }
};

