// Parse GPT-4 response and prepare for image generation
const response = $input.first().json;
const previousData = $('Format for GPT-4').first().json;

const analysis = JSON.parse(response.choices[0].message.content);

// Map mood to emoji
const moodEmojis = {
  'Great': 'üòä',
  'Good': 'üòå',
  'Neutral': 'üòê',
  'Low': 'üòî',
  'Productive': 'üî•'
};

const moodWithEmoji = `${moodEmojis[analysis.mood] || 'üòê'} ${analysis.mood}`;

return {
  json: {
    title: analysis.title,
    summary: analysis.summary,
    mood: analysis.mood,
    moodWithEmoji: moodWithEmoji,
    tags: analysis.tags,
    imagePrompt: analysis.imagePrompt,
    highlights: analysis.highlights,
    date: previousData.date,
    dateFormatted: previousData.dateFormatted,
    totalMessages: previousData.totalMessages,
    participants: previousData.participants
  }
};

// ============================================
// GENERATE IMAGE WITH DALL-E 3
// ============================================
// ‚ö†Ô∏è UPDATE THIS API KEY
const OPENAI_API_KEY = 'YOUR_OPENAI_API_KEY';
// ============================================

const data = $input.first().json;

// Enhance the prompt for better artistic results
const enhancedPrompt = `${data.imagePrompt}. Artistic digital illustration, dreamy atmosphere, soft lighting, no text or words, suitable for a personal journal.`;

try {
  const response = await fetch('https://api.openai.com/v1/images/generations', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${OPENAI_API_KEY}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      model: 'dall-e-3',
      prompt: enhancedPrompt.substring(0, 4000),
      n: 1,
      size: '1024x1024',
      quality: 'standard',
      response_format: 'b64_json'
    })
  });

  const result = await response.json();

  if (result.error) {
    return {
      json: {
        ...data,
        imageBase64: null,
        imageError: result.error.message
      }
    };
  }

  return {
    json: {
      ...data,
      imageBase64: result.data[0].b64_json,
      imageError: null
    }
  };

} catch (error) {
  return {
    json: {
      ...data,
      imageBase64: null,
      imageError: error.message
    }
  };
}

// ============================================
// UPLOAD IMAGE TO CLOUDINARY
// ============================================
// ‚ö†Ô∏è UPDATE THESE VALUES
const CLOUDINARY_CLOUD_NAME = 'YOUR_CLOUD_NAME';
const CLOUDINARY_API_KEY = 'YOUR_API_KEY';
const CLOUDINARY_API_SECRET = 'YOUR_API_SECRET';
// ============================================

const data = $input.first().json;

if (!data.imageBase64) {
  return {
    json: {
      ...data,
      imageUrl: null,
      uploadError: data.imageError || 'No image to upload'
    }
  };
}

try {
  // Create signature for Cloudinary
  const timestamp = Math.floor(Date.now() / 1000);
  const folder = 'visual-journal';
  const publicId = `diary_${data.date}`;

  // Build signature string
  const signatureString = `folder=${folder}&public_id=${publicId}&timestamp=${timestamp}${CLOUDINARY_API_SECRET}`;

  // Create SHA1 hash
  const crypto = require('crypto');
  const signature = crypto.createHash('sha1').update(signatureString).digest('hex');

  // Upload to Cloudinary
  const formData = new URLSearchParams();
  formData.append('file', `data:image/png;base64,${data.imageBase64}`);
  formData.append('api_key', CLOUDINARY_API_KEY);
  formData.append('timestamp', timestamp.toString());
  formData.append('signature', signature);
  formData.append('folder', folder);
  formData.append('public_id', publicId);

  const response = await fetch(
    `https://api.cloudinary.com/v1_1/${CLOUDINARY_CLOUD_NAME}/image/upload`,
    {
      method: 'POST',
      body: formData
    }
  );

  const result = await response.json();

  if (result.error) {
    return {
      json: {
        ...data,
        imageUrl: null,
        uploadError: result.error.message
      }
    };
  }

  return {
    json: {
      ...data,
      imageUrl: result.secure_url,
      imagePublicId: result.public_id,
      uploadError: null
    }
  };

} catch (error) {
  return {
    json: {
      ...data,
      imageUrl: null,
      uploadError: error.message
    }
  };
}

// Final response - entry created successfully
const data = $input.first().json;
const previousData = $('Upload to Cloudinary').first().json;

return {
  json: {
    success: true,
    message: 'Diary entry created successfully!',
    entry: {
      title: previousData.title,
      date: previousData.date,
      mood: previousData.moodWithEmoji,
      messageCount: previousData.totalMessages,
      hasImage: !!previousData.imageUrl,
      notionPageId: data.id,
      notionUrl: data.url
    }
  }
};

// No messages today - skip entry
const data = $input.first().json;

return {
  json: {
    success: false,
    message: 'No messages found for today. Skipping diary entry.',
    date: data.date || new Date().toISOString().split('T')[0]
  }
};
