// Calculate Date Ranges based on current timestamp
const now = new Date();
const oneWeek = 7 * 24 * 60 * 60 * 1000;

function getIsoDate(date) {
  return date.toISOString();
}

// 1. Current Week
const currentEnd = now;
const currentStart = new Date(now.getTime() - oneWeek);

// 2. Last Month (Same week)
const lastMonthEnd = new Date(now);
lastMonthEnd.setMonth(lastMonthEnd.getMonth() - 1);
const lastMonthStart = new Date(lastMonthEnd.getTime() - oneWeek);

// 3. Last Quarter (Same week)
const lastQEnd = new Date(now);
lastQEnd.setMonth(lastQEnd.getMonth() - 3);
const lastQStart = new Date(lastQEnd.getTime() - oneWeek);

// 4. Last Year (Same week)
const lastYearEnd = new Date(now);
lastYearEnd.setFullYear(lastYearEnd.getFullYear() - 1);
const lastYearStart = new Date(lastYearEnd.getTime() - oneWeek);

return {
  current: { after: getIsoDate(currentStart), before: getIsoDate(currentEnd) },
  lastMonth: { after: getIsoDate(lastMonthStart), before: getIsoDate(lastMonthEnd) },
  lastQuarter: { after: getIsoDate(lastQStart), before: getIsoDate(lastQEnd) },
  lastYear: { after: getIsoDate(lastYearStart), before: getIsoDate(lastYearEnd) }
};

// --- Input Retrieval ---
// We retrieve the Threshold from the Global Config node
const threshold = $('Global Configuration').first().json.trend_threshold || 5;

// Retrieve sales data safely (default to empty array if no data found)
const currentOrders = $('Fetch Current Week Sales').all().map(i => i.json) || [];
const lastMonthOrders = $('Fetch Last Month Sales').all().map(i => i.json) || [];
const lastYearOrders = $('Fetch Last Year Sales').all().map(i => i.json) || [];

// --- Helper Functions ---

// 1. Calculate Revenue, Count and SKU tally
function calculateMetrics(orders) {
  if (!orders || orders.length === 0) {
    return { revenue: 0, orders: 0, aov: 0, skus: {} };
  }

  let revenue = 0;
  let skuCounts = {};

  orders.forEach(order => {
    const orderTotal = parseFloat(order.total) || 0;
    revenue += orderTotal;

    if (order.line_items && Array.isArray(order.line_items)) {
      order.line_items.forEach(item => {
        skuCounts[item.name] = (skuCounts[item.name] || 0) + (item.quantity || 1);
      });
    }
  });

  return {
    revenue: revenue,
    orders: orders.length,
    aov: orders.length > 0 ? (revenue / orders.length) : 0,
    skus: skuCounts
  };
}

// 2. Identify Top SKU
function getTopSku(skuMap) {
  if (!skuMap || Object.keys(skuMap).length === 0) return 'None';
  let sorted = Object.entries(skuMap).sort((a,b) => b[1] - a[1]);
  return sorted.length > 0 ? `${sorted[0][0]} (${sorted[0][1]})` : 'None';
}

// 3. Determine Trend (No Emojis)
function calcTrend(current, historical) {
  if (!historical || historical === 0) {
    if (current === 0) return { percent: '0%', trend: 'Flat' };
    return { percent: 'N/A', trend: 'New' };
  }

  const diff = ((current - historical) / historical) * 100;

  let trend = 'Flat';
  if (diff > threshold) trend = 'Increase';
  if (diff < -threshold) trend = 'Decrease';

  return { percent: diff.toFixed(1) + '%', trend: trend };
}

// --- Main Execution ---

const currStats = calculateMetrics(currentOrders);
const lmStats = calculateMetrics(lastMonthOrders);
const lyStats = calculateMetrics(lastYearOrders);

return {
  revenue: {
    current: currStats.revenue.toFixed(2),
    vsMonth: calcTrend(currStats.revenue, lmStats.revenue),
    vsYear: calcTrend(currStats.revenue, lyStats.revenue)
  },
  orders: {
    current: currStats.orders,
    vsMonth: calcTrend(currStats.orders, lmStats.orders),
    vsYear: calcTrend(currStats.orders, lyStats.orders)
  },
  topSku: getTopSku(currStats.skus)
};
