const lead = $json.lead || {};

const missingFields = [];
if (!lead.email) missingFields.push("email");
if (!lead.message) missingFields.push("message");

if (!lead.firstName && !lead.lastName && !lead.company) {
  missingFields.push("firstName/lastName/company (at least one)");
}

// Basic spam checks (optional, but useful)
const isLikelySpam =
  typeof lead.message === "string" &&
  lead.message.length < 5; // too short, suspicious

const isValid = missingFields.length === 0 && !isLikelySpam;

return [
  {
    json: {
      lead,
      isValid,
      missingFields,
      isLikelySpam,
      errorMessage: !isValid
        ? (missingFields.length > 0
            ? `Missing required fields: ${missingFields.join(", ")}`
            : "Message looks too short / suspicious.")
        : null,
    },
  },
];

