// If body arrives as a JSON string (text/plain), parse it. - To make sure that the next nodes received normalized data object
const b = $json.body;

if (typeof b === "string") {
  try {
    const parsed = JSON.parse(b);
    return [{ json: { ...$json, ...parsed } }];
  } catch (e) {
    return [{ json: { ...$json, parseError: "Invalid JSON in body", rawBody: b } }];
  }
}

return [{ json: $json }];
