// 1. Get Data from the Mock/API
const comments = $("Get FB Comments").first().json.data || [];
let pastWinners = [];
try {
  pastWinners = $("Get Past Winners").all().map(i => i.json);
} catch (e) {
  pastWinners = [];
}

// 2. Create Blocklist (Users who won in last 30 days)
// FIX: We access "Facebook ID" directly using brackets (because of the space)
// We also use .filter(id => id) to skip incomplete records (like the second item in your data)
const winnerBlocklist = new Set(
    pastWinners
    .map(w => w["Facebook ID"]) 
    .filter(id => id)
);

// 3. Filter Out Past Winners & Short Comments
const eligibleForAI = comments.filter(c => {
  if (winnerBlocklist.has(c.from.id)) return false;
  if (!c.message || c.message.length < 2) return false;
  return true;
});

// 4. Return INDIVIDUAL ITEMS
if (eligibleForAI.length === 0) {
    return [{json: {abort: true, reason: "No eligible new users found"}}];
}

return eligibleForAI.map(c => ({
    json: {
        abort: false,
        id: c.from.id,
        name: c.from.name,
        text: c.message
    }
}));

// 1. Get all analyzed items
const items = $input.all();

// 2. Filter for POSITIVE sentiment
const positiveComments = items.filter(item => {
    // FIX: Access the nested path 'sentimentAnalysis.category'
    // Use optional chaining (?.) to prevent crashes if the field is missing
    const s = item.json.sentimentAnalysis?.category;

    // Check if sentiment exists and is Positive
    return s && s.toString().toLowerCase().includes('positive');
});

// 3. Validation
if (positiveComments.length === 0) {
   return [{json: {error: true, message: "AI found no positive human comments"}}];
}

// 4. Random Selection
const randomWinner = positiveComments[Math.floor(Math.random() * positiveComments.length)];

return [{
  json: {
    error: false,
    winner_id: randomWinner.json.id,
    winner_name: randomWinner.json.name,
    winner_comment: randomWinner.json.text,
    // FIX: Map the output correctly using the same nested path
    sentiment: randomWinner.json.sentimentAnalysis?.category
  }
}];

