const items = $input.all();
const results = [];

for (const item of items) {
  const binaryKey = Object.keys(item.binary || {})[0];
  if (binaryKey && item.binary[binaryKey]) {
    const audioData = item.binary[binaryKey];
    const sizeInBytes = audioData.data ? Buffer.from(audioData.data, 'base64').length : 0;
    const sizeInKB = (sizeInBytes / 1024).toFixed(2);

    // Determine language from binary key
    let language = 'Unknown';
    if (binaryKey.includes('spanish')) language = 'Spanish';
    else if (binaryKey.includes('french')) language = 'French';
    else if (binaryKey.includes('german')) language = 'German';

    results.push({
      json: {
        ...item.json,
        language: language,
        audioSizeKB: sizeInKB,
        audioFormat: 'mp3',
        generatedAt: new Date().toISOString()
      },
      binary: item.binary
    });
  }
}

return results;

