// Process the binary audio response and add metadata
const item = $input.item;

// Get the current language data from the loop
const languageCode = item.json.languageCode;
const language = item.json.language;

// Create filename for the audio file
const filename = `audio_${languageCode}.mp3`;

// Return the item with metadata and binary data
return {
  json: {
    language: language,
    languageCode: languageCode,
    filename: filename,
    success: true
  },
  binary: item.binary
};
