const item = items[0];
const audioData = item.binary?.audioData;

if (!audioData) {
  return [{
    json: {
      isValid: false,
      error: 'No audio data received',
      fileSize: 0
    }
  }];
}

const fileSize = audioData.data ? Buffer.from(audioData.data, 'base64').length : 0;
const minSize = 1000;
const maxSize = 50 * 1024 * 1024;

const isValid = fileSize >= minSize && fileSize <= maxSize;

return [{
  json: {
    isValid: isValid,
    fileSize: fileSize,
    fileSizeKB: Math.round(fileSize / 1024),
    mimeType: audioData.mimeType || 'audio/mpeg',
    fileName: audioData.fileName || 'audio.mp3',
    error: !isValid ? 'File size ' + Math.round(fileSize / 1024) + 'KB is outside valid range (1KB - 50MB)' : null
  },
  binary: {
    audioData: audioData
  }
}];
