// Analyze historical transaction patterns using statistical methods
// Input: query should contain transaction data as JSON string

try {
  // Parse the incoming transaction data
  const transactions = typeof query === 'string' ? JSON.parse(query) : query;

  if (!Array.isArray(transactions) || transactions.length === 0) {
    return 'No transaction data provided for analysis';
  }

  // Extract transaction amounts
  const amounts = transactions.map(t => parseFloat(t.amount || 0));

  // Calculate statistical measures
  const mean = amounts.reduce((sum, val) => sum + val, 0) / amounts.length;
  const variance = amounts.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / amounts.length;
  const stdDev = Math.sqrt(variance);

  // Calculate frequency distribution
  const frequencyMap = {};
  transactions.forEach(t => {
    const category = t.category || 'uncategorized';
    frequencyMap[category] = (frequencyMap[category] || 0) + 1;
  });

  // Identify outliers (values beyond 2 standard deviations)
  const outliers = transactions.filter(t => {
    const amount = parseFloat(t.amount || 0);
    return Math.abs(amount - mean) > 2 * stdDev;
  });

  // Build analysis result
  const analysis = {
    totalTransactions: transactions.length,
    statistics: {
      mean: mean.toFixed(2),
      standardDeviation: stdDev.toFixed(2),
      min: Math.min(...amounts).toFixed(2),
      max: Math.max(...amounts).toFixed(2)
    },
    frequencyDistribution: frequencyMap,
    outliers: outliers.map(t => ({
      id: t.id,
      amount: t.amount,
      category: t.category,
      deviationFromMean: (parseFloat(t.amount) - mean).toFixed(2)
    })),
    anomalyCount: outliers.length
  };

  return JSON.stringify(analysis, null, 2);

} catch (error) {
  return `Error analyzing patterns: ${error.message}`;
}
