// Extract time series safely
const series = $json["Time Series (Daily)"];

if (!series) {
  return null;
}

// Sort dates oldest â†’ newest
const dates = Object.keys(series).sort(
  (a, b) => new Date(a) - new Date(b)
);

// Extract closing prices as numbers
const closes = dates.map(date =>
  Number(series[date]["4. close"])
);

// Keep only last 60 days
const last60Closes = closes.slice(-60);

return {
  json: {
    stock: $json["Meta Data"]["2. Symbol"],
    closes: last60Closes
  }
};

const closes = $json.closes;
const stock = $json.stock;

// ---------- Helpers ----------
const SMA = (arr, n) =>
  arr.slice(-n).reduce((a, b) => a + b, 0) / n;

const EMA = (arr, n) => {
  const k = 2 / (n + 1);
  let ema = arr[0];
  for (let i = 1; i < arr.length; i++) {
    ema = arr[i] * k + ema * (1 - k);
  }
  return ema;
};

// ---------- RSI (14) ----------
let gains = 0;
let losses = 0;

for (let i = closes.length - 15; i < closes.length - 1; i++) {
  const diff = closes[i + 1] - closes[i];
  if (diff >= 0) gains += diff;
  else losses -= diff;
}

const rs = gains / (losses || 1);
const rsi = 100 - 100 / (1 + rs);

// ---------- Moving Averages ----------
const sma20 = SMA(closes, 20);
const sma50 = SMA(closes, 50);

// ---------- MACD ----------
const ema12 = EMA(closes.slice(-26), 12);
const ema26 = EMA(closes.slice(-26), 26);
const macd = ema12 - ema26;
const macdSignal = EMA([macd], 9);

// ---------- Trade Signal ----------
let finalSignal = "Neutral";

if (rsi < 30 && macd > macdSignal) {
  finalSignal = "Buy";
} else if (rsi > 70 && macd < macdSignal) {
  finalSignal = "Sell";
}

return {
  json: {
    stock,
    rsi: Number(rsi.toFixed(2)),
    sma20: Number(sma20.toFixed(2)),
    sma50: Number(sma50.toFixed(2)),
    macd: Number(macd.toFixed(2)),
    macdSignal: Number(macdSignal.toFixed(2)),
    finalSignal,
    lastUpdated: new Date().toISOString()
  }
};
