parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Meeting Details Extraction",
  "type": "object",
  "properties": {
    "meeting": {
      "type": "object",
      "description": "Extracted meeting/event details from the message",
      "additionalProperties": false,
      "properties": {
        "title": {
          "type": ["string", "null"],
          "minLength": 1,
          "description": "Meeting title or subject"
        },
        "date": {
          "type": ["string", "null"],
          "pattern": "^\\d{4}-\\d{2}-\\d{2}$",
          "description": "Meeting date in YYYY-MM-DD format"
        },
        "time": {
          "type": ["string", "null"],
          "pattern": "^\\d{2}:\\d{2}$",
          "description": "Meeting time in HH:mm 24-hour format"
        },
        "location": {
          "type": ["string", "null"],
          "description": "Physical location or venue"
        },
        "meeting_link": {
          "type": ["string", "null"],
          "format": "uri",
          "description": "URL for virtual meeting or location map"
        },
        "attendees": {
          "type": ["string", "null"],
          "description": "Who is invited or attending"
        },
        "notes": {
          "type": ["string", "null"],
          "maxLength": 500,
          "description": "Additional context or instructions"
        }
      },
      "required": ["title", "date", "time"]
    },
    "reasoning": {
      "type": "string",
      "minLength": 1,
      "maxLength": 200,
      "description": "Explanation of how relative date/time terms were resolved"
    }
  },
  "required": ["meeting", "reasoning"],
  "additionalProperties": false,
  "examples": [
    {
      "meeting": {
        "title": "Q1 planning session",
        "date": "2025-03-12",
        "time": "14:30",
        "location": "main conference room",
        "meeting_link": "https://zoom.us/j/123456789",
        "attendees": "team",
        "notes": "bring department budgets and proposals"
      },
      "reasoning": "Explicit date 'March 12th' and time '2:30 PM' provided"
    }
  ]
}

parameters.errorObject:
={{ JSON.stringify({
  error: {
    message: 'AI agent failed to normalize event strings',
    execution: $execution,
    workflow: $workflow
  },
  timestamp: new Date().toISOString()
}) }}
