// Parse and validate incoming webhook request
// Extract task metadata, regulatory constraints, cost ceilings, latency SLOs, quality expectations, and tenant ID

const items = $input.all();
const results = [];

for (const item of items) {
  const body = item.json.body || item.json;

  // Initialize validation errors array
  const validationErrors = [];

  // Parse and validate required fields
  const prompt = body.prompt || body.query || body.text;
  if (!prompt || typeof prompt !== 'string' || prompt.trim().length === 0) {
    validationErrors.push('Missing or invalid prompt/query/text');
  }

  const tenantId = body.tenantId || body.tenant_id || body.tenant;
  if (!tenantId) {
    validationErrors.push('Missing tenant ID');
  }

  // Parse task metadata
  const taskMetadata = {
    taskId: body.taskId || body.task_id || `task_${Date.now()}`,
    taskType: body.taskType || body.task_type || 'general',
    priority: body.priority || 'medium',
    timestamp: new Date().toISOString()
  };

  // Parse regulatory constraints
  const regulatoryConstraints = {
    dataResidency: body.dataResidency || body.data_residency || 'any',
    complianceRequirements: body.complianceRequirements || body.compliance_requirements || [],
    piiHandling: body.piiHandling || body.pii_handling || 'standard',
    dataClassification: body.dataClassification || body.data_classification || 'internal'
  };

  // Parse cost ceilings
  const costCeilings = {
    maxCostPerRequest: parseFloat(body.maxCostPerRequest || body.max_cost_per_request || 0.10),
    budgetLimit: parseFloat(body.budgetLimit || body.budget_limit || 100.0),
    costOptimizationEnabled: body.costOptimizationEnabled !== false
  };

  // Parse latency SLOs
  const latencySLOs = {
    maxLatencyMs: parseInt(body.maxLatencyMs || body.max_latency_ms || 5000),
    p95LatencyMs: parseInt(body.p95LatencyMs || body.p95_latency_ms || 3000),
    p99LatencyMs: parseInt(body.p99LatencyMs || body.p99_latency_ms || 4000),
    timeoutMs: parseInt(body.timeoutMs || body.timeout_ms || 30000)
  };

  // Parse quality expectations
  const qualityExpectations = {
    minConfidenceScore: parseFloat(body.minConfidenceScore || body.min_confidence_score || 0.7),
    requireFactChecking: body.requireFactChecking || body.require_fact_checking || false,
    hallucinationTolerance: body.hallucinationTolerance || body.hallucination_tolerance || 'low',
    outputFormat: body.outputFormat || body.output_format || 'json',
    maxTokens: parseInt(body.maxTokens || body.max_tokens || 2000),
    temperature: parseFloat(body.temperature || 0.7)
  };

  // Validate numeric ranges
  if (costCeilings.maxCostPerRequest < 0) {
    validationErrors.push('maxCostPerRequest must be non-negative');
  }

  if (latencySLOs.maxLatencyMs < 100 || latencySLOs.maxLatencyMs > 60000) {
    validationErrors.push('maxLatencyMs must be between 100 and 60000');
  }

  if (qualityExpectations.minConfidenceScore < 0 || qualityExpectations.minConfidenceScore > 1) {
    validationErrors.push('minConfidenceScore must be between 0 and 1');
  }

  if (qualityExpectations.temperature < 0 || qualityExpectations.temperature > 2) {
    validationErrors.push('temperature must be between 0 and 2');
  }

  // Build validated request object
  const validatedRequest = {
    prompt,
    tenantId,
    taskMetadata,
    regulatoryConstraints,
    costCeilings,
    latencySLOs,
    qualityExpectations,
    validationErrors,
    isValid: validationErrors.length === 0,
    rawRequest: body
  };

  results.push({
    json: validatedRequest
  });
}

return results;

// Analyze Prompt Complexity
// This code analyzes incoming prompts for token count, semantic complexity, domain classification, and processing time estimation

const items = $input.all();
const results = [];

for (const item of items) {
  const prompt = item.json.prompt || item.json.text || item.json.message || '';

  // Token count estimation (rough approximation: ~4 chars per token)
  const tokenCount = Math.ceil(prompt.length / 4);

  // Semantic complexity score (0-100)
  // Based on: sentence count, avg word length, unique words, punctuation density
  const sentences = prompt.split(/[.!?]+/).filter(s => s.trim().length > 0);
  const words = prompt.split(/\s+/).filter(w => w.length > 0);
  const uniqueWords = new Set(words.map(w => w.toLowerCase()));
  const avgWordLength = words.reduce((sum, w) => sum + w.length, 0) / (words.length || 1);
  const punctuationCount = (prompt.match(/[,;:()\[\]{}"']/g) || []).length;
  const punctuationDensity = punctuationCount / (words.length || 1);

  const complexityScore = Math.min(100, Math.round(
    (sentences.length * 5) +
    (avgWordLength * 8) +
    ((uniqueWords.size / (words.length || 1)) * 30) +
    (punctuationDensity * 20)
  ));

  // Domain classification
  const domainKeywords = {
    technical: ['code', 'function', 'algorithm', 'database', 'api', 'programming', 'software', 'debug', 'error', 'system'],
    creative: ['write', 'story', 'poem', 'creative', 'imagine', 'describe', 'narrative', 'character', 'plot'],
    analytical: ['analyze', 'compare', 'evaluate', 'assess', 'calculate', 'determine', 'measure', 'statistics', 'data'],
    conversational: ['hello', 'hi', 'thanks', 'please', 'help', 'question', 'what', 'how', 'why', 'explain'],
    business: ['strategy', 'market', 'revenue', 'customer', 'sales', 'business', 'profit', 'growth', 'roi']
  };

  const lowerPrompt = prompt.toLowerCase();
  const domainScores = {};

  for (const [domain, keywords] of Object.entries(domainKeywords)) {
    const matchCount = keywords.filter(kw => lowerPrompt.includes(kw)).length;
    domainScores[domain] = matchCount;
  }

  const primaryDomain = Object.entries(domainScores)
    .sort((a, b) => b[1] - a[1])[0]?.[0] || 'general';

  // Estimated processing time (in seconds)
  // Base time + token factor + complexity factor
  const baseTime = 0.5;
  const tokenFactor = tokenCount * 0.01;
  const complexityFactor = complexityScore * 0.02;
  const estimatedProcessingTime = Math.round((baseTime + tokenFactor + complexityFactor) * 100) / 100;

  results.push({
    json: {
      ...item.json,
      promptAnalysis: {
        tokenCount,
        complexityScore,
        primaryDomain,
        domainScores,
        estimatedProcessingTime,
        promptLength: prompt.length,
        sentenceCount: sentences.length,
        wordCount: words.length,
        uniqueWordRatio: Math.round((uniqueWords.size / (words.length || 1)) * 100) / 100
      }
    }
  });
}

return results;

// Score & Rank Models based on multiple criteria
// Inputs: health checks, historical performance, prompt complexity, config

const items = $input.all();

// Extract data from merged inputs
const healthChecks = [];
const historicalPerformance = [];
let promptComplexity = {};
let config = {};

// Parse input items to separate health checks, historical data, and config
for (const item of items) {
  if (item.json.type === 'health_check') {
    healthChecks.push(item.json);
  } else if (item.json.type === 'historical_performance') {
    historicalPerformance.push(item.json);
  } else if (item.json.type === 'prompt_complexity') {
    promptComplexity = item.json;
  } else if (item.json.type === 'config') {
    config = item.json;
  }
}

// Default configuration values
const costWeight = config.costWeight || 0.25;
const latencyWeight = config.latencyWeight || 0.30;
const qualityWeight = config.qualityWeight || 0.30;
const healthWeight = config.healthWeight || 0.15;

const maxCostPerRequest = config.maxCostPerRequest || 0.10;
const maxLatencySLO = config.maxLatencySLO || 5000; // ms
const minQualityScore = config.minQualityScore || 0.7;

// Model definitions with base characteristics
const modelCharacteristics = {
  'azure_openai': {
    baseCost: 0.03,
    baseLatency: 2000,
    baseQuality: 0.92,
    complexityMultiplier: 1.2
  },
  'aws_bedrock': {
    baseCost: 0.025,
    baseLatency: 1800,
    baseQuality: 0.90,
    complexityMultiplier: 1.15
  },
  'google_vertex': {
    baseCost: 0.028,
    baseLatency: 2200,
    baseQuality: 0.91,
    complexityMultiplier: 1.18
  },
  'local_model': {
    baseCost: 0.001,
    baseLatency: 3500,
    baseQuality: 0.75,
    complexityMultiplier: 1.5
  }
};

// Function to calculate model score
function calculateModelScore(modelName, health, historical, complexity) {
  const characteristics = modelCharacteristics[modelName];
  if (!characteristics) return 0;

  // Health score (0-1)
  const healthScore = health.status === 'healthy' ? 1.0 : 
                      health.status === 'degraded' ? 0.5 : 0.0;

  // Cost score (inverse - lower cost is better)
  const estimatedCost = characteristics.baseCost * (complexity.score || 1);
  const costScore = Math.max(0, 1 - (estimatedCost / maxCostPerRequest));

  // Latency score (inverse - lower latency is better)
  const estimatedLatency = characteristics.baseLatency * 
                          (complexity.score || 1) * 
                          characteristics.complexityMultiplier;
  const latencyScore = Math.max(0, 1 - (estimatedLatency / maxLatencySLO));

  // Quality score (adjusted by historical performance)
  const historicalQuality = historical?.averageQuality || characteristics.baseQuality;
  const qualityScore = Math.min(1, historicalQuality / minQualityScore);

  // Weighted total score
  const totalScore = (
    (healthScore * healthWeight) +
    (costScore * costWeight) +
    (latencyScore * latencyWeight) +
    (qualityScore * qualityWeight)
  );

  return {
    modelName,
    totalScore: Math.round(totalScore * 1000) / 1000,
    breakdown: {
      healthScore: Math.round(healthScore * 1000) / 1000,
      costScore: Math.round(costScore * 1000) / 1000,
      latencyScore: Math.round(latencyScore * 1000) / 1000,
      qualityScore: Math.round(qualityScore * 1000) / 1000
    },
    estimates: {
      cost: Math.round(estimatedCost * 10000) / 10000,
      latency: Math.round(estimatedLatency),
      quality: Math.round(historicalQuality * 1000) / 1000
    },
    health: health.status,
    meetsConstraints: (
      estimatedCost <= maxCostPerRequest &&
      estimatedLatency <= maxLatencySLO &&
      historicalQuality >= minQualityScore
    )
  };
}

// Score all models
const modelScores = [];

for (const health of healthChecks) {
  const modelName = health.model;
  const historical = historicalPerformance.find(h => h.model === modelName);

  const score = calculateModelScore(modelName, health, historical, promptComplexity);
  modelScores.push(score);
}

// Sort by total score (descending)
modelScores.sort((a, b) => b.totalScore - a.totalScore);

// Add rank
modelScores.forEach((score, index) => {
  score.rank = index + 1;
});

// Filter to only models that meet constraints
const eligibleModels = modelScores.filter(m => m.meetsConstraints && m.health !== 'unhealthy');
const ineligibleModels = modelScores.filter(m => !m.meetsConstraints || m.health === 'unhealthy');

// Return results
return [{
  json: {
    type: 'model_ranking',
    timestamp: new Date().toISOString(),
    promptComplexity: promptComplexity.score || 1,
    constraints: {
      maxCostPerRequest,
      maxLatencySLO,
      minQualityScore
    },
    weights: {
      cost: costWeight,
      latency: latencyWeight,
      quality: qualityWeight,
      health: healthWeight
    },
    rankedModels: modelScores,
    eligibleModels,
    ineligibleModels,
    recommendedModel: eligibleModels.length > 0 ? eligibleModels[0].modelName : null,
    totalModelsEvaluated: modelScores.length,
    eligibleCount: eligibleModels.length
  }
}];

// Apply Policy Routing - Filter and re-rank models based on regulatory constraints

const items = $input.all();
const results = [];

for (const item of items) {
  const rankedModels = item.json.rankedModels || [];
  const promptAnalysis = item.json.promptAnalysis || {};
  const policyConstraints = item.json.policyConstraints || {};

  // Extract sensitivity flags
  const containsPII = promptAnalysis.containsPII || false;
  const containsPHI = promptAnalysis.containsPHI || false;
  const containsFinancial = promptAnalysis.containsFinancial || false;
  const dataClassification = promptAnalysis.dataClassification || 'public';

  // Define regulatory requirements for each model
  const modelCompliance = {
    'azure-openai': {
      hipaaCompliant: true,
      gdprCompliant: true,
      soc2Certified: true,
      dataResidency: ['US', 'EU'],
      piiHandling: true
    },
    'aws-bedrock': {
      hipaaCompliant: true,
      gdprCompliant: true,
      soc2Certified: true,
      dataResidency: ['US', 'EU', 'APAC'],
      piiHandling: true
    },
    'google-vertex': {
      hipaaCompliant: true,
      gdprCompliant: true,
      soc2Certified: true,
      dataResidency: ['US', 'EU'],
      piiHandling: true
    },
    'local-model': {
      hipaaCompliant: false,
      gdprCompliant: false,
      soc2Certified: false,
      dataResidency: ['on-premise'],
      piiHandling: false
    }
  };

  // Filter models based on policy constraints
  let filteredModels = rankedModels.filter(model => {
    const compliance = modelCompliance[model.provider] || {};

    // Check HIPAA compliance for PHI data
    if (containsPHI && !compliance.hipaaCompliant) {
      console.log(`Filtering out ${model.provider}: Not HIPAA compliant`);
      return false;
    }

    // Check GDPR compliance for PII data
    if (containsPII && policyConstraints.requireGDPR && !compliance.gdprCompliant) {
      console.log(`Filtering out ${model.provider}: Not GDPR compliant`);
      return false;
    }

    // Check data residency requirements
    if (policyConstraints.dataResidency && !compliance.dataResidency.includes(policyConstraints.dataResidency)) {
      console.log(`Filtering out ${model.provider}: Data residency requirement not met`);
      return false;
    }

    // Check PII handling capability
    if ((containsPII || containsPHI) && !compliance.piiHandling) {
      console.log(`Filtering out ${model.provider}: Cannot handle PII/PHI data`);
      return false;
    }

    // Check classification level restrictions
    if (dataClassification === 'confidential' && model.provider === 'local-model') {
      console.log(`Filtering out ${model.provider}: Not approved for confidential data`);
      return false;
    }

    return true;
  });

  // Re-rank filtered models with compliance bonus
  filteredModels = filteredModels.map(model => {
    const compliance = modelCompliance[model.provider] || {};
    let complianceBonus = 0;

    // Add bonus points for compliance features
    if (compliance.hipaaCompliant) complianceBonus += 5;
    if (compliance.gdprCompliant) complianceBonus += 5;
    if (compliance.soc2Certified) complianceBonus += 3;

    // Adjust score with compliance bonus
    const adjustedScore = (model.score || 0) + complianceBonus;

    return {
      ...model,
      complianceScore: complianceBonus,
      originalScore: model.score,
      score: adjustedScore,
      complianceDetails: compliance
    };
  });

  // Sort by adjusted score (descending)
  filteredModels.sort((a, b) => b.score - a.score);

  // Select the top model after policy filtering
  const selectedModel = filteredModels.length > 0 ? filteredModels[0] : null;

  if (!selectedModel) {
    throw new Error('No models available that meet policy constraints');
  }

  console.log(`Policy routing selected: ${selectedModel.provider} (score: ${selectedModel.score})`);

  results.push({
    json: {
      ...item.json,
      filteredModels,
      selectedModel,
      policyApplied: true,
      modelsFiltered: rankedModels.length - filteredModels.length,
      policyReason: {
        containsPII,
        containsPHI,
        containsFinancial,
        dataClassification,
        constraints: policyConstraints
      }
    }
  });
}

return results;

// Rewrite and optimize prompt for Azure OpenAI format
const item = $input.item.json;

// Extract prompt and configuration from previous nodes
const userPrompt = item.prompt || item.userPrompt || item.message || '';
const complexity = item.complexity || 'medium';
const selectedModel = item.selectedModel || 'gpt-4';
const maxTokens = item.maxTokens || 2000;
const temperature = item.temperature || 0.7;

// Build Azure OpenAI compatible request format
const azureRequest = {
  messages: [
    {
      role: 'system',
      content: 'You are a helpful AI assistant. Provide accurate, concise, and well-structured responses.'
    },
    {
      role: 'user',
      content: userPrompt
    }
  ],
  temperature: temperature,
  max_tokens: maxTokens,
  top_p: 0.95,
  frequency_penalty: 0,
  presence_penalty: 0,
  stop: null
};

// Adjust parameters based on complexity
if (complexity === 'high') {
  azureRequest.temperature = Math.min(temperature, 0.5);
  azureRequest.max_tokens = Math.max(maxTokens, 3000);
} else if (complexity === 'low') {
  azureRequest.temperature = Math.min(temperature + 0.2, 1.0);
  azureRequest.max_tokens = Math.min(maxTokens, 1000);
}

// Add model-specific optimizations
if (selectedModel.includes('gpt-4')) {
  azureRequest.messages[0].content += ' Use your advanced reasoning capabilities to provide detailed analysis.';
} else if (selectedModel.includes('gpt-3.5')) {
  azureRequest.messages[0].content += ' Provide clear and efficient responses.';
}

// Return the formatted request
return {
  json: {
    ...item,
    azureRequest: azureRequest,
    provider: 'azure',
    formattedPrompt: userPrompt,
    systemMessage: azureRequest.messages[0].content,
    rewrittenAt: new Date().toISOString()
  }
};

// Rewrite and optimize prompt for AWS Bedrock format
const items = $input.all();
const outputItems = [];

for (const item of items) {
  const originalPrompt = item.json.prompt || item.json.userPrompt || '';
  const complexity = item.json.complexity || 'medium';
  const selectedModel = item.json.selectedModel || 'anthropic.claude-v2';
  const maxTokens = item.json.maxTokens || 2048;
  const temperature = item.json.temperature || 0.7;

  // Extract model provider from model ID
  const modelProvider = selectedModel.split('.')[0];

  // Rewrite prompt based on AWS Bedrock best practices
  let rewrittenPrompt = originalPrompt;

  // Add system context for better results
  let systemPrompt = 'You are a helpful AI assistant. Provide accurate, concise, and relevant responses.';

  // Adjust based on complexity
  if (complexity === 'high') {
    systemPrompt += ' Take your time to think through complex problems step by step.';
  }

  // Format request body based on model provider
  let bedrockRequestBody = {};

  if (modelProvider === 'anthropic') {
    // Claude format
    bedrockRequestBody = {
      prompt: `\n\nHuman: ${rewrittenPrompt}\n\nAssistant:`,
      max_tokens_to_sample: maxTokens,
      temperature: temperature,
      top_p: 0.9,
      top_k: 250,
      stop_sequences: ['\n\nHuman:']
    };
  } else if (modelProvider === 'ai21') {
    // AI21 Jurassic format
    bedrockRequestBody = {
      prompt: rewrittenPrompt,
      maxTokens: maxTokens,
      temperature: temperature,
      topP: 0.9,
      stopSequences: [],
      countPenalty: {
        scale: 0
      },
      presencePenalty: {
        scale: 0
      },
      frequencyPenalty: {
        scale: 0
      }
    };
  } else if (modelProvider === 'amazon') {
    // Amazon Titan format
    bedrockRequestBody = {
      inputText: rewrittenPrompt,
      textGenerationConfig: {
        maxTokenCount: maxTokens,
        temperature: temperature,
        topP: 0.9,
        stopSequences: []
      }
    };
  } else if (modelProvider === 'cohere') {
    // Cohere format
    bedrockRequestBody = {
      prompt: rewrittenPrompt,
      max_tokens: maxTokens,
      temperature: temperature,
      p: 0.9,
      k: 0,
      stop_sequences: []
    };
  } else {
    // Default format
    bedrockRequestBody = {
      prompt: rewrittenPrompt,
      max_tokens: maxTokens,
      temperature: temperature
    };
  }

  outputItems.push({
    json: {
      ...item.json,
      rewrittenPrompt: rewrittenPrompt,
      systemPrompt: systemPrompt,
      bedrockModelId: selectedModel,
      bedrockRequestBody: bedrockRequestBody,
      bedrockRequestBodyString: JSON.stringify(bedrockRequestBody),
      provider: 'aws_bedrock',
      modelProvider: modelProvider,
      inferenceConfig: {
        maxTokens: maxTokens,
        temperature: temperature,
        topP: 0.9
      }
    }
  });
}

return outputItems;

// Rewrite and optimize prompt for Google Vertex AI format
const item = $input.item.json;

// Extract prompt and configuration from previous nodes
const originalPrompt = item.prompt || item.userPrompt || item.query || '';
const complexity = item.complexity || 'medium';
const selectedModel = item.selectedModel || 'gemini-pro';
const maxTokens = item.maxTokens || 2048;
const temperature = item.temperature || 0.7;

// Map complexity to Vertex AI parameters
const complexityConfig = {
  low: { temperature: 0.3, topP: 0.8, topK: 20 },
  medium: { temperature: 0.7, topP: 0.9, topK: 40 },
  high: { temperature: 0.9, topP: 0.95, topK: 60 }
};

const config = complexityConfig[complexity] || complexityConfig.medium;

// Optimize prompt for Vertex AI (Gemini models prefer clear, structured prompts)
let optimizedPrompt = originalPrompt;

// Add context markers for better Gemini understanding
if (!optimizedPrompt.includes('Context:') && !optimizedPrompt.includes('Task:')) {
  optimizedPrompt = `Task: ${optimizedPrompt}\n\nPlease provide a clear, accurate, and well-structured response.`;
}

// Build Vertex AI request payload
const vertexPayload = {
  instances: [
    {
      content: optimizedPrompt
    }
  ],
  parameters: {
    temperature: temperature || config.temperature,
    maxOutputTokens: maxTokens,
    topP: config.topP,
    topK: config.topK,
    // Safety settings for Vertex AI
    safetySettings: [
      {
        category: 'HARM_CATEGORY_HATE_SPEECH',
        threshold: 'BLOCK_MEDIUM_AND_ABOVE'
      },
      {
        category: 'HARM_CATEGORY_DANGEROUS_CONTENT',
        threshold: 'BLOCK_MEDIUM_AND_ABOVE'
      },
      {
        category: 'HARM_CATEGORY_SEXUALLY_EXPLICIT',
        threshold: 'BLOCK_MEDIUM_AND_ABOVE'
      },
      {
        category: 'HARM_CATEGORY_HARASSMENT',
        threshold: 'BLOCK_MEDIUM_AND_ABOVE'
      }
    ]
  }
};

// Return optimized payload for Google Vertex AI
return {
  json: {
    ...item,
    originalPrompt: originalPrompt,
    optimizedPrompt: optimizedPrompt,
    vertexPayload: vertexPayload,
    provider: 'google-vertex',
    model: selectedModel,
    rewriteTimestamp: new Date().toISOString(),
    promptOptimizations: [
      'Added task structure',
      'Applied Vertex AI safety settings',
      'Configured generation parameters',
      'Set complexity-based tuning'
    ]
  }
};

// Rewrite and optimize prompt for local model format
// Adapts the prompt to the specific local model API structure

const items = $input.all();
const results = [];

for (const item of items) {
  const originalPrompt = item.json.prompt || '';
  const modelConfig = item.json.selectedModel || {};
  const complexity = item.json.complexity || {};
  const metadata = item.json.metadata || {};

  // Extract local model specific configuration
  const localModelName = modelConfig.localModelName || 'llama2';
  const maxTokens = modelConfig.maxTokens || 2048;
  const temperature = modelConfig.temperature || 0.7;

  // Optimize prompt for local model
  let optimizedPrompt = originalPrompt;

  // Add system context if needed for local models
  const systemContext = metadata.systemContext || '';
  if (systemContext) {
    optimizedPrompt = `System: ${systemContext}\n\nUser: ${originalPrompt}`;
  }

  // Format for common local model APIs (Ollama, LM Studio, etc.)
  const localModelRequest = {
    model: localModelName,
    prompt: optimizedPrompt,
    options: {
      temperature: temperature,
      num_predict: maxTokens,
      top_p: 0.9,
      top_k: 40,
      repeat_penalty: 1.1
    },
    stream: false
  };

  // Add complexity-based optimizations
  if (complexity.level === 'high') {
    localModelRequest.options.temperature = Math.min(temperature + 0.1, 1.0);
    localModelRequest.options.num_predict = Math.min(maxTokens * 1.5, 4096);
  }

  results.push({
    json: {
      ...item.json,
      rewrittenPrompt: optimizedPrompt,
      localModelRequest: localModelRequest,
      originalPrompt: originalPrompt,
      modelProvider: 'local',
      rewriteTimestamp: new Date().toISOString()
    }
  });
}

return results;

// Circuit Breaker Logic for Multi-Cloud LLM Orchestration
// Tracks failure counts per model, opens circuit after threshold, determines retry/fallback

const item = $input.item.json;

// Configuration
const FAILURE_THRESHOLD = 3;
const CIRCUIT_OPEN_DURATION_MS = 60000; // 1 minute
const MAX_RETRIES = 2;

// Initialize circuit breaker state (in production, use external state store)
const circuitState = $('Workflow Configuration').item.json.circuitState || {};

// Extract model information and response status
const modelName = item.selectedModel || item.model || 'unknown';
const isSuccess = item.statusCode >= 200 && item.statusCode < 300;
const responseTime = item.responseTime || 0;
const errorMessage = item.error || '';

// Initialize model state if not exists
if (!circuitState[modelName]) {
  circuitState[modelName] = {
    failureCount: 0,
    lastFailureTime: null,
    circuitStatus: 'closed', // closed, open, half-open
    consecutiveSuccesses: 0,
    totalRequests: 0,
    totalFailures: 0
  };
}

const modelState = circuitState[modelName];
modelState.totalRequests++;

// Check current circuit status
let shouldRetry = false;
let shouldFallback = false;
let retryCount = item.retryCount || 0;

// If circuit is open, check if enough time has passed to try half-open
if (modelState.circuitStatus === 'open') {
  const timeSinceFailure = Date.now() - modelState.lastFailureTime;

  if (timeSinceFailure >= CIRCUIT_OPEN_DURATION_MS) {
    // Move to half-open state for testing
    modelState.circuitStatus = 'half-open';
    console.log(`Circuit for ${modelName} moved to HALF-OPEN state`);
  } else {
    // Circuit still open, trigger immediate fallback
    shouldFallback = true;
    console.log(`Circuit for ${modelName} is OPEN. Triggering fallback.`);
  }
}

// Process current request result
if (isSuccess) {
  // Success handling
  modelState.consecutiveSuccesses++;

  if (modelState.circuitStatus === 'half-open' && modelState.consecutiveSuccesses >= 2) {
    // Close circuit after successful half-open tests
    modelState.circuitStatus = 'closed';
    modelState.failureCount = 0;
    console.log(`Circuit for ${modelName} CLOSED after successful recovery`);
  } else if (modelState.circuitStatus === 'closed') {
    // Reset failure count on success in closed state
    modelState.failureCount = 0;
  }

  shouldRetry = false;
  shouldFallback = false;

} else {
  // Failure handling
  modelState.failureCount++;
  modelState.totalFailures++;
  modelState.lastFailureTime = Date.now();
  modelState.consecutiveSuccesses = 0;

  console.log(`Failure detected for ${modelName}. Count: ${modelState.failureCount}/${FAILURE_THRESHOLD}`);

  // Check if we should open the circuit
  if (modelState.failureCount >= FAILURE_THRESHOLD) {
    modelState.circuitStatus = 'open';
    console.log(`Circuit for ${modelName} OPENED due to ${FAILURE_THRESHOLD} consecutive failures`);
    shouldFallback = true;
    shouldRetry = false;
  } else if (retryCount < MAX_RETRIES) {
    // Still within retry limits and circuit not open
    shouldRetry = true;
    shouldFallback = false;
    retryCount++;
  } else {
    // Exceeded retry limit
    shouldFallback = true;
    shouldRetry = false;
    console.log(`Max retries (${MAX_RETRIES}) exceeded for ${modelName}. Triggering fallback.`);
  }
}

// Calculate failure rate
const failureRate = modelState.totalRequests > 0 
  ? (modelState.totalFailures / modelState.totalRequests * 100).toFixed(2)
  : 0;

// Prepare output
return {
  json: {
    ...item,
    circuitBreaker: {
      modelName,
      circuitStatus: modelState.circuitStatus,
      failureCount: modelState.failureCount,
      failureThreshold: FAILURE_THRESHOLD,
      consecutiveSuccesses: modelState.consecutiveSuccesses,
      totalRequests: modelState.totalRequests,
      totalFailures: modelState.totalFailures,
      failureRate: `${failureRate}%`,
      lastFailureTime: modelState.lastFailureTime,
      shouldRetry,
      shouldFallback,
      retryCount,
      maxRetries: MAX_RETRIES,
      timestamp: Date.now()
    },
    shouldRetry,
    shouldFallback,
    retryCount,
    circuitState // Pass updated state forward
  }
};

// Normalize model outputs into a consistent format
// Extract text, metadata, and provider-specific fields into a unified structure

const items = $input.all();
const normalizedOutputs = [];

for (const item of items) {
  const rawResponse = item.json;

  // Initialize normalized structure
  const normalized = {
    text: '',
    provider: '',
    model: '',
    metadata: {
      tokensUsed: 0,
      promptTokens: 0,
      completionTokens: 0,
      latencyMs: 0,
      finishReason: '',
      requestId: ''
    },
    providerSpecific: {},
    timestamp: new Date().toISOString(),
    success: true
  };

  // Detect provider and normalize accordingly
  if (rawResponse.provider === 'azure' || rawResponse.choices) {
    // Azure OpenAI format
    normalized.provider = 'azure';
    normalized.text = rawResponse.choices?.[0]?.message?.content || rawResponse.choices?.[0]?.text || '';
    normalized.model = rawResponse.model || 'gpt-4';
    normalized.metadata.tokensUsed = rawResponse.usage?.total_tokens || 0;
    normalized.metadata.promptTokens = rawResponse.usage?.prompt_tokens || 0;
    normalized.metadata.completionTokens = rawResponse.usage?.completion_tokens || 0;
    normalized.metadata.finishReason = rawResponse.choices?.[0]?.finish_reason || '';
    normalized.metadata.requestId = rawResponse.id || '';
    normalized.providerSpecific = {
      systemFingerprint: rawResponse.system_fingerprint,
      created: rawResponse.created
    };
  } 
  else if (rawResponse.provider === 'aws' || rawResponse.body) {
    // AWS Bedrock format
    normalized.provider = 'aws';
    const body = typeof rawResponse.body === 'string' ? JSON.parse(rawResponse.body) : rawResponse.body;
    normalized.text = body.completion || body.results?.[0]?.outputText || '';
    normalized.model = rawResponse.modelId || 'claude-v2';
    normalized.metadata.tokensUsed = body.usage?.total_tokens || (body.inputTextTokenCount || 0) + (body.results?.[0]?.tokenCount || 0);
    normalized.metadata.promptTokens = body.inputTextTokenCount || 0;
    normalized.metadata.completionTokens = body.results?.[0]?.tokenCount || 0;
    normalized.metadata.finishReason = body.stop_reason || body.results?.[0]?.completionReason || '';
    normalized.metadata.requestId = rawResponse.ResponseMetadata?.RequestId || '';
    normalized.providerSpecific = {
      stopReason: body.stop_reason,
      amazonBedrockInvocationMetrics: rawResponse.ResponseMetadata?.HTTPHeaders?.['x-amzn-bedrock-invocation-latency']
    };
  }
  else if (rawResponse.provider === 'google' || rawResponse.predictions) {
    // Google Vertex AI format
    normalized.provider = 'google';
    normalized.text = rawResponse.predictions?.[0]?.content || rawResponse.predictions?.[0]?.candidates?.[0]?.content || '';
    normalized.model = rawResponse.deployedModelId || 'text-bison';
    normalized.metadata.tokensUsed = rawResponse.metadata?.tokenMetadata?.totalTokens || 0;
    normalized.metadata.promptTokens = rawResponse.metadata?.tokenMetadata?.inputTokens || 0;
    normalized.metadata.completionTokens = rawResponse.metadata?.tokenMetadata?.outputTokens || 0;
    normalized.metadata.finishReason = rawResponse.predictions?.[0]?.safetyAttributes?.blocked ? 'blocked' : 'stop';
    normalized.metadata.requestId = rawResponse.metadata?.requestId || '';
    normalized.providerSpecific = {
      safetyAttributes: rawResponse.predictions?.[0]?.safetyAttributes,
      citationMetadata: rawResponse.predictions?.[0]?.citationMetadata
    };
  }
  else if (rawResponse.provider === 'local' || rawResponse.response) {
    // Local model format (e.g., Ollama, LM Studio)
    normalized.provider = 'local';
    normalized.text = rawResponse.response || rawResponse.text || rawResponse.output || '';
    normalized.model = rawResponse.model || 'local-llm';
    normalized.metadata.tokensUsed = rawResponse.eval_count || 0;
    normalized.metadata.promptTokens = rawResponse.prompt_eval_count || 0;
    normalized.metadata.completionTokens = rawResponse.eval_count || 0;
    normalized.metadata.finishReason = rawResponse.done ? 'stop' : 'length';
    normalized.metadata.requestId = rawResponse.created_at || '';
    normalized.providerSpecific = {
      totalDuration: rawResponse.total_duration,
      loadDuration: rawResponse.load_duration,
      evalDuration: rawResponse.eval_duration
    };
  }
  else {
    // Generic fallback
    normalized.provider = rawResponse.provider || 'unknown';
    normalized.text = rawResponse.text || rawResponse.content || rawResponse.output || JSON.stringify(rawResponse);
    normalized.model = rawResponse.model || 'unknown';
    normalized.success = false;
  }

  // Calculate latency if available
  if (rawResponse.startTime && rawResponse.endTime) {
    normalized.metadata.latencyMs = rawResponse.endTime - rawResponse.startTime;
  } else if (rawResponse.latency) {
    normalized.metadata.latencyMs = rawResponse.latency;
  }

  normalizedOutputs.push({ json: normalized });
}

return normalizedOutputs;

// Calculate Telemetry Metrics
const item = $input.item.json;

// Extract model response data
const modelResponse = item.modelResponse || {};
const selectedModel = item.selectedModel || 'unknown';
const startTime = item.startTime || Date.now();

// Calculate token usage
const promptTokens = modelResponse.usage?.prompt_tokens || 0;
const completionTokens = modelResponse.usage?.completion_tokens || 0;
const totalTokens = promptTokens + completionTokens;

// Calculate actual latency (in milliseconds)
const endTime = Date.now();
const actualLatency = endTime - startTime;

// Model pricing per 1K tokens (adjust based on actual pricing)
const modelPricing = {
  'azure-openai': { prompt: 0.0015, completion: 0.002 },
  'aws-bedrock': { prompt: 0.0008, completion: 0.0024 },
  'google-vertex': { prompt: 0.00025, completion: 0.0005 },
  'local-model': { prompt: 0, completion: 0 }
};

// Get pricing for selected model
const pricing = modelPricing[selectedModel] || { prompt: 0, completion: 0 };

// Calculate cost
const promptCost = (promptTokens / 1000) * pricing.prompt;
const completionCost = (completionTokens / 1000) * pricing.completion;
const totalCost = promptCost + completionCost;

// Create telemetry metrics object
const telemetryMetrics = {
  totalTokens: totalTokens,
  promptTokens: promptTokens,
  completionTokens: completionTokens,
  actualLatency: actualLatency,
  latencySeconds: (actualLatency / 1000).toFixed(3),
  cost: {
    promptCost: parseFloat(promptCost.toFixed(6)),
    completionCost: parseFloat(completionCost.toFixed(6)),
    totalCost: parseFloat(totalCost.toFixed(6)),
    currency: 'USD'
  },
  timestamp: new Date().toISOString(),
  model: selectedModel,
  startTime: startTime,
  endTime: endTime
};

// Return enriched item with telemetry metrics
return {
  json: {
    ...item,
    telemetryMetrics: telemetryMetrics
  }
};

// Assess Hallucination Risk
// Analyzes LLM response for potential hallucinations using multiple heuristics
// Produces a risk score from 0 (low risk) to 1 (high risk)

const items = $input.all();
const results = [];

for (const item of items) {
  const response = item.json.response || item.json.output || '';
  const prompt = item.json.prompt || item.json.input || '';
  const modelName = item.json.selectedModel || 'unknown';

  // Initialize risk factors
  let riskScore = 0;
  const riskFactors = [];

  // 1. Response Consistency Check
  // Look for contradictions within the response
  const sentences = response.split(/[.!?]+/).filter(s => s.trim().length > 0);
  let contradictionScore = 0;

  // Check for common contradiction patterns
  const contradictionPatterns = [
    { pattern: /(however|but|although|despite).*?(not|never|no|none)/gi, weight: 0.1 },
    { pattern: /(always|never|all|none).*?(sometimes|maybe|possibly)/gi, weight: 0.15 },
    { pattern: /(yes|correct|true).*?(no|incorrect|false)/gi, weight: 0.2 }
  ];

  for (const { pattern, weight } of contradictionPatterns) {
    const matches = response.match(pattern);
    if (matches && matches.length > 0) {
      contradictionScore += weight * matches.length;
      riskFactors.push(`Contradiction pattern detected (${matches.length} instances)`);
    }
  }

  riskScore += Math.min(contradictionScore, 0.3);

  // 2. Confidence Markers Analysis
  // Lack of confidence markers or excessive hedging indicates uncertainty
  const uncertaintyMarkers = [
    'might', 'maybe', 'possibly', 'perhaps', 'could be', 'may be',
    'i think', 'i believe', 'probably', 'likely', 'unclear',
    'not sure', 'uncertain', 'approximately', 'roughly'
  ];

  let uncertaintyCount = 0;
  const lowerResponse = response.toLowerCase();

  for (const marker of uncertaintyMarkers) {
    const regex = new RegExp(marker, 'gi');
    const matches = lowerResponse.match(regex);
    if (matches) {
      uncertaintyCount += matches.length;
    }
  }

  // High uncertainty marker density suggests hallucination risk
  const uncertaintyDensity = uncertaintyCount / Math.max(sentences.length, 1);
  if (uncertaintyDensity > 0.3) {
    riskScore += 0.2;
    riskFactors.push(`High uncertainty marker density: ${uncertaintyDensity.toFixed(2)}`);
  } else if (uncertaintyDensity > 0.15) {
    riskScore += 0.1;
    riskFactors.push(`Moderate uncertainty marker density: ${uncertaintyDensity.toFixed(2)}`);
  }

  // 3. Factual Grounding Indicators
  // Check for specific dates, numbers, citations, or references
  const groundingPatterns = [
    /\b(19|20)\d{2}\b/g, // Years
    /\b\d+(\.\d+)?%\b/g, // Percentages
    /\b\d+(\.\d+)?\s*(million|billion|thousand)\b/gi, // Large numbers
    /\b(according to|based on|research shows|studies indicate|data suggests)\b/gi, // Citations
    /\b(source:|reference:|citation:)\b/gi // Explicit references
  ];

  let groundingScore = 0;
  for (const pattern of groundingPatterns) {
    const matches = response.match(pattern);
    if (matches) {
      groundingScore += matches.length;
    }
  }

  // Low grounding in factual responses increases risk
  const groundingDensity = groundingScore / Math.max(sentences.length, 1);
  if (groundingDensity < 0.1 && response.length > 200) {
    riskScore += 0.15;
    riskFactors.push('Low factual grounding indicators');
  }

  // 4. Specificity vs Vagueness
  // Vague responses are more likely to be hallucinated
  const vagueTerms = [
    'some', 'many', 'various', 'several', 'often', 'sometimes',
    'generally', 'typically', 'usually', 'commonly', 'frequently',
    'thing', 'stuff', 'something', 'somewhere'
  ];

  let vaguenessCount = 0;
  for (const term of vagueTerms) {
    const regex = new RegExp(`\\b${term}\\b`, 'gi');
    const matches = lowerResponse.match(regex);
    if (matches) {
      vaguenessCount += matches.length;
    }
  }

  const vaguenessDensity = vaguenessCount / Math.max(sentences.length, 1);
  if (vaguenessDensity > 0.4) {
    riskScore += 0.15;
    riskFactors.push(`High vagueness density: ${vaguenessDensity.toFixed(2)}`);
  }

  // 5. Response Length Analysis
  // Extremely short or verbose responses can indicate issues
  if (response.length < 50 && prompt.length > 100) {
    riskScore += 0.1;
    riskFactors.push('Response too short for complex prompt');
  } else if (response.length > 5000 && sentences.length > 50) {
    riskScore += 0.05;
    riskFactors.push('Unusually verbose response');
  }

  // 6. Repetition Detection
  // Excessive repetition suggests model confusion
  const words = response.toLowerCase().split(/\s+/);
  const wordFrequency = {};

  for (const word of words) {
    if (word.length > 4) { // Only count meaningful words
      wordFrequency[word] = (wordFrequency[word] || 0) + 1;
    }
  }

  let repetitionScore = 0;
  for (const [word, count] of Object.entries(wordFrequency)) {
    if (count > 5) {
      repetitionScore += (count - 5) * 0.02;
    }
  }

  if (repetitionScore > 0.1) {
    riskScore += Math.min(repetitionScore, 0.15);
    riskFactors.push('Excessive word repetition detected');
  }

  // 7. Prompt-Response Alignment
  // Check if response actually addresses the prompt
  const promptKeywords = prompt.toLowerCase()
    .split(/\s+/)
    .filter(w => w.length > 4 && !['what', 'when', 'where', 'which', 'would', 'could', 'should'].includes(w));

  let alignmentScore = 0;
  for (const keyword of promptKeywords.slice(0, 10)) { // Check top 10 keywords
    if (lowerResponse.includes(keyword)) {
      alignmentScore++;
    }
  }

  const alignmentRatio = alignmentScore / Math.max(promptKeywords.slice(0, 10).length, 1);
  if (alignmentRatio < 0.3) {
    riskScore += 0.2;
    riskFactors.push(`Low prompt-response alignment: ${(alignmentRatio * 100).toFixed(0)}%`);
  }

  // Normalize final risk score to 0-1 range
  riskScore = Math.min(Math.max(riskScore, 0), 1);

  // Determine risk level
  let riskLevel = 'low';
  if (riskScore > 0.7) {
    riskLevel = 'critical';
  } else if (riskScore > 0.5) {
    riskLevel = 'high';
  } else if (riskScore > 0.3) {
    riskLevel = 'medium';
  }

  results.push({
    json: {
      ...item.json,
      hallucinationRisk: {
        score: parseFloat(riskScore.toFixed(3)),
        level: riskLevel,
        factors: riskFactors,
        metrics: {
          contradictionScore: parseFloat(contradictionScore.toFixed(3)),
          uncertaintyDensity: parseFloat(uncertaintyDensity.toFixed(3)),
          groundingDensity: parseFloat(groundingDensity.toFixed(3)),
          vaguenessDensity: parseFloat(vaguenessDensity.toFixed(3)),
          alignmentRatio: parseFloat(alignmentRatio.toFixed(3)),
          repetitionScore: parseFloat(repetitionScore.toFixed(3))
        },
        assessedAt: new Date().toISOString(),
        modelName: modelName
      }
    }
  });
}

return results;

// Calculate Confidence Score based on multiple factors
// Inputs: model certainty, response completeness, hallucination risk, quality indicators
// Output: confidence score between 0 and 1

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Extract input factors (with defaults if not present)
  const modelCertainty = data.modelCertainty || 0.5;
  const responseCompleteness = data.responseCompleteness || 0.5;
  const hallucinationRisk = data.hallucinationRisk || 0.5;
  const qualityIndicators = data.qualityIndicators || {};

  // Calculate quality score from indicators
  let qualityScore = 0.5;
  if (Object.keys(qualityIndicators).length > 0) {
    const scores = Object.values(qualityIndicators).filter(v => typeof v === 'number');
    if (scores.length > 0) {
      qualityScore = scores.reduce((sum, val) => sum + val, 0) / scores.length;
    }
  }

  // Weighted confidence calculation
  // Higher hallucination risk reduces confidence
  const weights = {
    modelCertainty: 0.3,
    responseCompleteness: 0.25,
    hallucinationRiskInverse: 0.25,
    qualityScore: 0.2
  };

  const confidenceScore = (
    (modelCertainty * weights.modelCertainty) +
    (responseCompleteness * weights.responseCompleteness) +
    ((1 - hallucinationRisk) * weights.hallucinationRiskInverse) +
    (qualityScore * weights.qualityScore)
  );

  // Ensure score is between 0 and 1
  const normalizedConfidence = Math.max(0, Math.min(1, confidenceScore));

  // Determine confidence level
  let confidenceLevel = 'low';
  if (normalizedConfidence >= 0.8) {
    confidenceLevel = 'high';
  } else if (normalizedConfidence >= 0.6) {
    confidenceLevel = 'medium';
  }

  results.push({
    json: {
      ...data,
      confidenceScore: normalizedConfidence,
      confidenceLevel: confidenceLevel,
      confidenceFactors: {
        modelCertainty: modelCertainty,
        responseCompleteness: responseCompleteness,
        hallucinationRisk: hallucinationRisk,
        qualityScore: qualityScore
      },
      confidenceCalculatedAt: new Date().toISOString()
    }
  });
}

return results;

// Detect Anomalies - Compare current metrics against historical baselines
// Uses z-score statistical method to identify anomalies

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Extract current metrics
  const currentMetrics = {
    latency: data.latency || 0,
    cost: data.cost || 0,
    tokenUsage: data.tokenUsage || 0,
    errorRate: data.errorRate || 0
  };

  // Extract historical baselines (mean and standard deviation)
  const baselines = data.historicalBaselines || {
    latency: { mean: 1000, stdDev: 200 },
    cost: { mean: 0.05, stdDev: 0.01 },
    tokenUsage: { mean: 500, stdDev: 100 },
    errorRate: { mean: 0.02, stdDev: 0.01 }
  };

  // Calculate z-scores for each metric
  const calculateZScore = (value, mean, stdDev) => {
    if (stdDev === 0) return 0;
    return (value - mean) / stdDev;
  };

  const zScores = {
    latency: calculateZScore(currentMetrics.latency, baselines.latency.mean, baselines.latency.stdDev),
    cost: calculateZScore(currentMetrics.cost, baselines.cost.mean, baselines.cost.stdDev),
    tokenUsage: calculateZScore(currentMetrics.tokenUsage, baselines.tokenUsage.mean, baselines.tokenUsage.stdDev),
    errorRate: calculateZScore(currentMetrics.errorRate, baselines.errorRate.mean, baselines.errorRate.stdDev)
  };

  // Determine anomaly flags and severity
  // Z-score thresholds: |z| > 3 = critical, |z| > 2 = high, |z| > 1.5 = medium
  const anomalies = [];
  let maxSeverity = 'normal';

  Object.keys(zScores).forEach(metric => {
    const z = Math.abs(zScores[metric]);
    let severity = 'normal';
    let isAnomaly = false;

    if (z > 3) {
      severity = 'critical';
      isAnomaly = true;
    } else if (z > 2) {
      severity = 'high';
      isAnomaly = true;
    } else if (z > 1.5) {
      severity = 'medium';
      isAnomaly = true;
    }

    if (isAnomaly) {
      anomalies.push({
        metric: metric,
        currentValue: currentMetrics[metric],
        expectedMean: baselines[metric].mean,
        zScore: zScores[metric],
        severity: severity,
        deviation: ((currentMetrics[metric] - baselines[metric].mean) / baselines[metric].mean * 100).toFixed(2) + '%'
      });

      // Update max severity
      const severityLevels = { normal: 0, medium: 1, high: 2, critical: 3 };
      if (severityLevels[severity] > severityLevels[maxSeverity]) {
        maxSeverity = severity;
      }
    }
  });

  // Prepare output
  results.push({
    json: {
      ...data,
      anomalyDetection: {
        hasAnomalies: anomalies.length > 0,
        anomalyCount: anomalies.length,
        maxSeverity: maxSeverity,
        anomalies: anomalies,
        zScores: zScores,
        currentMetrics: currentMetrics,
        timestamp: new Date().toISOString()
      }
    }
  });
}

return results;

