// Calculate Multi-Factor Risk Score
// Combines analysis results from metadata, forgery, semantic, pattern, and enrichment analyses

const item = $input.item.json;

// Extract risk scores from different analysis components
const metadataAnalysis = item.metadataAnalysis || {};
const forgeryAnalysis = item.forgeryAnalysis || {};
const semanticAnalysis = item.semanticAnalysis || {};
const patternAnalysis = item.patternAnalysis || {};
const enrichmentAnalysis = item.enrichmentAnalysis || {};

// Define weights for each analysis component
const weights = {
  forgery: 0.35,      // Forgery detection is highest priority
  semantic: 0.25,     // Semantic contradictions are critical
  pattern: 0.20,      // Historical patterns provide context
  enrichment: 0.15,   // Entity enrichment adds validation
  metadata: 0.05      // Metadata provides baseline info
};

// Extract individual risk scores (0-100 scale)
const forgeryScore = forgeryAnalysis.overallRiskScore || 0;
const semanticScore = semanticAnalysis.overallRiskScore || 0;
const patternScore = patternAnalysis.overallRiskScore || 0;
const enrichmentScore = enrichmentAnalysis.overallRiskScore || 0;
const metadataScore = metadataAnalysis.riskScore || 0;

// Calculate weighted risk score
const weightedRiskScore = (
  (forgeryScore * weights.forgery) +
  (semanticScore * weights.semantic) +
  (patternScore * weights.pattern) +
  (enrichmentScore * weights.enrichment) +
  (metadataScore * weights.metadata)
);

// Extract confidence scores from each analysis
const forgeryConfidence = forgeryAnalysis.averageConfidence || 70;
const semanticConfidence = semanticAnalysis.averageConfidence || 70;
const patternConfidence = patternAnalysis.averageConfidence || 70;
const enrichmentConfidence = enrichmentAnalysis.averageConfidence || 70;
const metadataConfidence = metadataAnalysis.confidence || 70;

// Calculate overall confidence (weighted average)
const overallConfidence = (
  (forgeryConfidence * weights.forgery) +
  (semanticConfidence * weights.semantic) +
  (patternConfidence * weights.pattern) +
  (enrichmentConfidence * weights.enrichment) +
  (metadataConfidence * weights.metadata)
);

// Calculate confidence interval
const confidenceMargin = (100 - overallConfidence) / 2;
const confidenceInterval = {
  lower: Math.max(0, weightedRiskScore - confidenceMargin),
  upper: Math.min(100, weightedRiskScore + confidenceMargin)
};

// Generate explainable risk factors
const riskFactors = [];

// Add forgery indicators
if (forgeryAnalysis.indicators && forgeryAnalysis.indicators.length > 0) {
  forgeryAnalysis.indicators.forEach(indicator => {
    if (indicator.severity === 'high' || indicator.severity === 'critical') {
      riskFactors.push({
        category: 'Forgery Detection',
        factor: indicator.indicator,
        severity: indicator.severity,
        impact: forgeryScore * weights.forgery,
        description: indicator.description,
        confidence: indicator.confidence
      });
    }
  });
}

// Add semantic contradictions
if (semanticAnalysis.contradictions && semanticAnalysis.contradictions.length > 0) {
  semanticAnalysis.contradictions.forEach(contradiction => {
    if (contradiction.severity === 'high' || contradiction.severity === 'critical') {
      riskFactors.push({
        category: 'Semantic Analysis',
        factor: contradiction.type,
        severity: contradiction.severity,
        impact: semanticScore * weights.semantic,
        description: contradiction.description,
        confidence: contradiction.confidence
      });
    }
  });
}

// Add historical pattern matches
if (patternAnalysis.patterns && patternAnalysis.patterns.length > 0) {
  patternAnalysis.patterns.forEach(pattern => {
    if (pattern.similarity > 70) {
      riskFactors.push({
        category: 'Historical Patterns',
        factor: pattern.patternType,
        severity: pattern.similarity > 90 ? 'critical' : 'high',
        impact: patternScore * weights.pattern,
        description: pattern.description,
        confidence: pattern.similarity
      });
    }
  });
}

// Add enrichment findings
if (enrichmentAnalysis.riskIndicators && enrichmentAnalysis.riskIndicators.length > 0) {
  enrichmentAnalysis.riskIndicators.forEach(indicator => {
    riskFactors.push({
      category: 'Entity Enrichment',
      factor: indicator.type,
      severity: indicator.severity,
      impact: enrichmentScore * weights.enrichment,
      description: indicator.description,
      confidence: indicator.confidence || 70
    });
  });
}

// Sort risk factors by impact (descending)
riskFactors.sort((a, b) => b.impact - a.impact);

// Determine risk level
let riskLevel = 'low';
let recommendation = 'Document appears legitimate. Proceed with standard processing.';

if (weightedRiskScore >= 90) {
  riskLevel = 'critical';
  recommendation = 'CRITICAL RISK: Document shows multiple severe fraud indicators. Immediate security team review required. Do not process.';
} else if (weightedRiskScore >= 70) {
  riskLevel = 'high';
  recommendation = 'HIGH RISK: Document contains significant fraud indicators. Mandatory compliance team review before processing.';
} else if (weightedRiskScore >= 30) {
  riskLevel = 'medium';
  recommendation = 'MEDIUM RISK: Document has some concerning elements. Recommend additional verification before processing.';
}

// Generate summary statistics
const summary = {
  totalIndicators: riskFactors.length,
  criticalIndicators: riskFactors.filter(f => f.severity === 'critical').length,
  highIndicators: riskFactors.filter(f => f.severity === 'high').length,
  mediumIndicators: riskFactors.filter(f => f.severity === 'medium').length,
  averageConfidence: overallConfidence,
  analysisTimestamp: new Date().toISOString()
};

// Return comprehensive risk assessment
return {
  json: {
    riskScore: Math.round(weightedRiskScore * 100) / 100,
    riskLevel: riskLevel,
    confidence: Math.round(overallConfidence * 100) / 100,
    confidenceInterval: {
      lower: Math.round(confidenceInterval.lower * 100) / 100,
      upper: Math.round(confidenceInterval.upper * 100) / 100
    },
    riskFactors: riskFactors,
    recommendation: recommendation,
    summary: summary,
    componentScores: {
      forgery: Math.round(forgeryScore * 100) / 100,
      semantic: Math.round(semanticScore * 100) / 100,
      pattern: Math.round(patternScore * 100) / 100,
      enrichment: Math.round(enrichmentScore * 100) / 100,
      metadata: Math.round(metadataScore * 100) / 100
    },
    weights: weights,
    // Preserve original analysis data
    metadataAnalysis: metadataAnalysis,
    forgeryAnalysis: forgeryAnalysis,
    semanticAnalysis: semanticAnalysis,
    patternAnalysis: patternAnalysis,
    enrichmentAnalysis: enrichmentAnalysis
  }
};

