function parseAnswers(input) {
  if (!input) return {};

  if (typeof input === "object") {
    return input;
  }

  if (typeof input === "string") {
    let obj = {};
    let parts = input
      .replace(/->/g, ":")
      .replace(/\n/g, ",")
      .split(",");

    for (let p of parts) {
      let [q, ans] = p.split(":").map(s => s.trim());
      if (q && ans) obj[q] = ans.toUpperCase();
    }
    return obj;
  }

  return {};
}

// ---- Extract Inputs
const item = $input.item?.json || {};

// ---- Student Details
const student = item.student || {
  name: null,
  rollNumber: null,
  class: null
};

// ---- Answers
const original = parseAnswers(item.chatInput || item.CorrectAnswer || {});
const user = parseAnswers(item.UserAnswers || {});

// ---- Evaluation
let correct = 0;
let incorrect = 0;
let results = [];
let binaryResult = {}; // üëà for Google Sheets (Q.1 = 1/0)

const total =
  Object.keys(original).length ||
  Object.keys(user).length ||
  0;

for (let i = 1; i <= total; i++) {
  const q = String(i);
  const correctAns = original[q] || "Not Available";
  const given = user[q] || "Not Attempted";

  const isCorrect = given === correctAns;
  const value = isCorrect ? 1 : 0;

  if (isCorrect) {
    correct++;
  } else {
    incorrect++;
  }

  results.push({
    question: i,
    given,
    correctAns,
    status: isCorrect ? "Correct" : "Incorrect",
    value // üëà 1 or 0
  });

  binaryResult[`Q.${i}`] = value; // üëà Q.1, Q.2, ...
}

// ---- Score
const scorePercentage =
  total > 0 ? ((correct / total) * 100).toFixed(2) : "0.00";

// ---- FINAL OUTPUT
return [
  {
    json: {
      student,
      totalQuestions: total,
      correct,
      incorrect,
      scorePercentage,
      results,        // detailed
      binaryResult    // üëà PERFECT FOR GOOGLE SHEETS
    }
  }
];

