// ===============================
// 1. GET INPUT
// ===============================
const input = items[0].json;
let content = input.message?.content || "";

// Remove ``` fences if present
content = content.replace(/```/g, "").trim();

// ===============================
// 2. EXTRACT STUDENT DETAILS
// ===============================
const student = {
  name: null,
  rollNumber: null,
  class: null
};

// Name
const nameMatch = content.match(/"name"\s*:\s*"([^"]+)"/i);
if (nameMatch) student.name = nameMatch[1].trim();

// Roll Number
const rollMatch = content.match(/"rollNumber"\s*:\s*"([^"]+)"/i);
if (rollMatch) student.rollNumber = rollMatch[1].trim();

// Class
const classMatch = content.match(/"class"\s*:\s*"([^"]+)"/i);
if (classMatch) student.class = classMatch[1].trim();

// ===============================
// 3. EXTRACT USER ANSWERS
// ===============================

// Regex to capture answers like "1:A", "1. A", "2 : B"
const regex = /(\d+)\s*[:\.]\s*([A-D])/g;

let match;
const pairs = [];

// Extract question-answer pairs
while ((match = regex.exec(content)) !== null) {
  pairs.push(`${match[1]}:${match[2]}`);
}

// Join into a single string
const userAnswersStr = pairs.join(", ");

// ===============================
// 4. FINAL OUTPUT
// ===============================
return [
  {
    json: {
      student,
      UserAnswers: userAnswersStr
    }
  }
];
