const ema = Number(items[2].json.values[0].ema);

// Trend logic
let trend = "NEUTRAL";
if (price > ema) trend = "BULLISH";
else if (price < ema) trend = "BEARISH";

// Momentum logic
let momentum = "NEUTRAL";
if (rsi >= 60) momentum = "BULLISH";
else if (rsi <= 40) momentum = "BEARISH";

return [
  {
    json: {
      price,
      ema,
      rsi,
      volume,
      trend,
      momentum
    }
  }
];

