/**
 * This node extracts readable text from a downloaded file.
 * Works best for TXT / HTML / MD files.
 * For PDF/DOCX it forwards base64 content to AI (still usable by OpenAI).
 */

const items = $input.all();

return items.map(item => {
  let extractedText = '';
  let fileName = '';
  let mimeType = '';

  // Google Drive download puts file in binary
  const binaryKeys = Object.keys(item.binary || {});

  if (binaryKeys.length === 0) {
    extractedText = 'No file found in binary data.';
  } else {
    const binaryData = item.binary[binaryKeys[0]];
    fileName = binaryData.fileName || 'unknown';
    mimeType = binaryData.mimeType || 'unknown';

    // Convert base64 ‚Üí string
    const buffer = Buffer.from(binaryData.data, 'base64');

    if (
      mimeType.includes('text') ||
      fileName.endsWith('.txt') ||
      fileName.endsWith('.md') ||
      fileName.endsWith('.html')
    ) {
      // Clean readable text files
      extractedText = buffer.toString('utf-8');
    } else {
      // For PDF / DOCX / others ‚Üí pass raw content to AI
      extractedText =
        `The following is raw extracted content from a ${fileName} contract file.\n` +
        `Analyze and summarize key terms, risks, and obligations:\n\n` +
        buffer.toString('utf-8');
    }
  }

  return {
    json: {
      fileName,
      mimeType,
      contractText: extractedText
    }
  };
});

/**
 * This code:
 * 1. Extracts AI text from LangChain output format
 * 2. Parses JSON safely
 * 3. Flattens data for alerts & Google Sheets
 */

function safeJsonParse(text) {
  try {
    return JSON.parse(text);
  } catch (e) {
    const match = text.match(/\{[\s\S]*\}/);
    if (!match) throw new Error("No JSON found");
    return JSON.parse(match[0]);
  }
}

return $input.all().map(item => {
  let rawText = "";

  try {
    rawText =
      item.json.output?.[0]
        ?.content?.find(c => c.type === "output_text")
        ?.text || "";
  } catch (e) {
    rawText = "";
  }

  let parsed;
  try {
    parsed = safeJsonParse(rawText);
  } catch (err) {
    return {
      json: {
        error: "Failed to parse AI response",
        rawResponse: rawText
      }
    };
  }

  function formatRisks(risksArray) {
    if (!Array.isArray(risksArray) || risksArray.length === 0) {
      return "No significant risks identified";
    }

    return risksArray
      .map(r => {
        if (typeof r === "string") return r;
        if (typeof r === "object") {
          return `${r.classification}: ${r.risk}`;
        }
        return "";
      })
      .filter(Boolean)
      .join("; ");
  }

  return {
    json: {
      // Contract Overview
      contractType: parsed.contractOverview?.contractType || "",
      partiesInvolved: (parsed.contractOverview?.partiesInvolved || []).join(", "),
      effectiveDate: parsed.contractOverview?.effectiveDate || "",
      contractDuration: parsed.contractOverview?.contractDuration || "",

      // Key Terms
      scopeOfServices: parsed.keyTerms?.scopeOfServices || "",
      pricingSummary: parsed.keyTerms?.pricingSummary || "",
      governingLaw: parsed.keyTerms?.governingLaw || "",

      // Payment
      fees: parsed.paymentClauses?.fees || "",
      billingCycle: parsed.paymentClauses?.billingCycle || "",
      latePaymentPenalties: parsed.paymentClauses?.latePaymentPenalties || "",

      // Renewal & Termination
      renewalType: parsed.renewalAndTermination?.renewalType || "",
      noticePeriod: parsed.renewalAndTermination?.noticePeriod || "",
      terminationConditions: parsed.renewalAndTermination?.terminationConditions || "",

      // Obligations
      obligationsPartyA: (parsed.obligations?.partyA || []).join("; "),
      obligationsPartyB: (parsed.obligations?.partyB || []).join("; "),

      // Risks (FIXED)
      financialRisks: formatRisks(parsed.risksAndPenalties?.financialRisks),
      legalRisks: formatRisks(parsed.risksAndPenalties?.legalRisks),
      operationalRisks: formatRisks(parsed.risksAndPenalties?.operationalRisks),

      // Dates
      importantDatesAndDeadlines: (parsed.importantDatesAndDeadlines || []).join(" | "),

      // Summary
      overallRiskLevel: parsed.overallRiskLevel || "Unknown",
      stakeholderSummary: parsed.summaryForStakeholders || ""
    }
  };
});

/**
 * Get data from Alert Teams Automatically node
 * This works regardless of which path (true/false) was taken
 */

// Get data from the IF node (Alert Teams Automatically)
const alertData = $('Alert Teams Automatically1').first().json;

return [{
  json: {
    // Add timestamp
    processedDate: new Date().toISOString(),
    processedDateTime: new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }),

    // Contract basic info
    contractType: alertData.contractType || '',
    partiesInvolved: alertData.partiesInvolved || '',
    effectiveDate: alertData.effectiveDate || '',
    contractDuration: alertData.contractDuration || '',

    // Financial details
    fees: alertData.fees || '',
    billingCycle: alertData.billingCycle || '',
    latePaymentPenalties: alertData.latePaymentPenalties || '',

    // Risk assessment
    overallRiskLevel: alertData.overallRiskLevel || 'Unknown',
    financialRisks: alertData.financialRisks || '',
    legalRisks: alertData.legalRisks || '',
    operationalRisks: alertData.operationalRisks || '',

    // Renewal & termination
    renewalType: alertData.renewalType || '',
    noticePeriod: alertData.noticePeriod || '',
    terminationConditions: alertData.terminationConditions || '',

    // Obligations
    obligationsPartyA: alertData.obligationsPartyA || '',
    obligationsPartyB: alertData.obligationsPartyB || '',

    // Other details
    scopeOfServices: alertData.scopeOfServices || '',
    pricingSummary: alertData.pricingSummary || '',
    governingLaw: alertData.governingLaw || '',
    importantDatesAndDeadlines: alertData.importantDatesAndDeadlines || '',
    stakeholderSummary: alertData.stakeholderSummary || '',

    // Email path taken
    emailType: alertData.overallRiskLevel === 'Medium' ? 'Risk Alert' : 'Info Only'
  }
}];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();
