const items = $input.all();
const formattedEngagements = [];

for (const item of items) {
  const eng = item.json.engagement;
  const meta = item.json.metadata || {};
  const assoc = item.json.associations || {};

  // Base engagement data
  const engagementData = {
    // Core IDs
    engagementId: eng.id,
    portalId: eng.portalId,
    dealId: assoc.dealIds?.[0] || null,

    // Type and Status
    type: eng.type,
    active: eng.active,

    // Timestamps
    createdAt: new Date(eng.createdAt).toISOString(),
    lastUpdated: new Date(eng.lastUpdated).toISOString(),
    timestamp: new Date(eng.timestamp * 1000).toISOString(),

    // Content - All available text fields
    title: meta.title || '',
    body: meta.body || '',
    bodyPreview: eng.bodyPreview || '',
    bodyPreviewHtml: eng.bodyPreviewHtml || '',
    bodyPreviewIsTruncated: eng.bodyPreviewIsTruncated || false,

    // Internal Notes (Important!)
    internalNotes: meta.internalMeetingNotes || meta.internalNotes || '',

    // Associations
    contactIds: assoc.contactIds || [],
    companyIds: assoc.companyIds || [],
    dealIds: assoc.dealIds || [],
    ownerIds: assoc.ownerIds || [],
    ticketIds: assoc.ticketIds || [],

    // Attachments
    attachments: item.json.attachments || [],
    hasAttachments: (item.json.attachments || []).length > 0,

    // Days calculation
    daysSinceEngagement: Math.floor((Date.now() - eng.timestamp * 1000) / (1000 * 60 * 60 * 24))
  };

  // Type-specific metadata
  if (eng.type === 'MEETING') {
    engagementData.meetingStartTime = meta.startTime ? new Date(meta.startTime * 1000).toISOString() : null;
    engagementData.meetingEndTime = meta.endTime ? new Date(meta.endTime * 1000).toISOString() : null;
    engagementData.meetingSource = meta.source || '';
    engagementData.guestEmails = meta.guestEmails || [];
    engagementData.attendeeOwnerIds = meta.attendeeOwnerIds || [];
  }

  if (eng.type === 'CALL') {
    engagementData.callDuration = meta.durationMilliseconds || 0;
    engagementData.callStatus = meta.status || '';
    engagementData.callDisposition = meta.disposition || '';
    engagementData.recordingUrl = meta.recordingUrl || '';
  }

  if (eng.type === 'EMAIL') {
    engagementData.emailSubject = meta.subject || '';
    engagementData.emailFrom = meta.from || {};
    engagementData.emailTo = meta.to || [];
    engagementData.emailCc = meta.cc || [];
    engagementData.emailBcc = meta.bcc || [];
    engagementData.emailStatus = meta.status || '';
  }

  if (eng.type === 'NOTE') {
    engagementData.noteBody = meta.body || eng.bodyPreview || '';
  }

  if (eng.type === 'TASK') {
    engagementData.taskSubject = meta.subject || '';
    engagementData.taskStatus = meta.status || '';
    engagementData.taskPriority = meta.priority || '';
    engagementData.taskType = meta.taskType || '';
    engagementData.forObjectType = meta.forObjectType || '';
  }

  formattedEngagements.push({ json: engagementData });
}

return formattedEngagements;

const items = $input.all();
const filteredDeals = [];

// Loop through each item (each item is already a deal)
for (const item of items) {
  const deal = item.json;
  const props = deal.properties;

  // Get associations
  const contactIds = deal.associations?.associatedVids || [];
  const companyIds = deal.associations?.associatedCompanyIds || [];

  // Extract only important fields
  const filteredDeal = {
    dealId: deal.dealId,
    portalId: deal.portalId,
    dealName: props.dealname?.value || 'Unnamed Deal',
    amount: parseFloat(props.amount?.value || 0),
    dealStage: props.dealstage?.value || 'unknown',
    createDate: props.createdate?.value ? new Date(parseInt(props.createdate.value)).toISOString() : null,
    closeDate: props.closedate?.value ? new Date(parseInt(props.closedate.value)).toISOString() : null,
    lastModifiedDate: props.hs_lastmodifieddate?.value ? new Date(parseInt(props.hs_lastmodifieddate.value)).toISOString() : null,
    lastContactedDate: props.notes_last_contacted?.value ? new Date(parseInt(props.notes_last_contacted.value)).toISOString() : null,
    numAssociatedContacts: parseInt(props.num_associated_contacts?.value || 0),
    daysSinceActivity: props.notes_last_contacted?.value ? 
      Math.floor((Date.now() - parseInt(props.notes_last_contacted.value)) / (1000 * 60 * 60 * 24)) :
      (props.hs_lastmodifieddate?.value ? 
        Math.floor((Date.now() - parseInt(props.hs_lastmodifieddate.value)) / (1000 * 60 * 60 * 24)) : 999),
    dealAgeDays: props.createdate?.value ? 
      Math.floor((Date.now() - parseInt(props.createdate.value)) / (1000 * 60 * 60 * 24)) : 0,
    isDeleted: deal.isDeleted || false,
    isClosed: ['closedwon', 'closedlost'].includes(props.dealstage?.value?.toLowerCase()),
    isActive: !['closedwon', 'closedlost'].includes(props.dealstage?.value?.toLowerCase())
  };

  // Add ownerId only if it exists
  if (props.hs_all_owner_ids?.value) {
    filteredDeal.ownerId = props.hs_all_owner_ids.value;
  }

  // Add contact IDs dynamically (only non-null)
  contactIds.forEach((contactId, index) => {
    if (contactId) {
      filteredDeal[`contactId${index + 1}`] = contactId;
    }
  });
  if (contactIds.length > 0) {
    filteredDeal.totalContacts = contactIds.length;
  }

  // Add company IDs dynamically (only non-null)
  companyIds.forEach((companyId, index) => {
    if (companyId) {
      filteredDeal[`companyId${index + 1}`] = companyId;
    }
  });
  if (companyIds.length > 0) {
    filteredDeal.totalCompanies = companyIds.length;
  }

  filteredDeals.push({ json: filteredDeal });
}

return filteredDeals;

const items = $input.all();
const results = [];

for (const item of items) {
  const aiOutput = item.json.output || item.json.response || item.json.text || '';

  // Helper function to extract value after a pattern with null safety
  const extract = (pattern, defaultValue = null) => {
    try {
      const match = aiOutput.match(pattern);
      return match && match[1] ? match[1].trim() : defaultValue;
    } catch (e) {
      return defaultValue;
    }
  };

  // Helper function to extract number
  const extractNumber = (pattern, defaultValue = null) => {
    try {
      const match = aiOutput.match(pattern);
      return match && match[1] ? parseInt(match[1]) : defaultValue;
    } catch (e) {
      return defaultValue;
    }
  };

  // Helper function to extract list items
  const extractList = (startPattern, endPattern) => {
    try {
      const regex = new RegExp(`${startPattern}([\\s\\S]*?)${endPattern}`, 'i');
      const match = aiOutput.match(regex);
      if (!match || !match[1]) return [];

      return match[1]
        .split(/\n/)
        .filter(line => line.trim().match(/^\d+\./))
        .map(line => line.replace(/^\d+\.\s*/, '').trim())
        .filter(item => item.length > 0);
    } catch (e) {
      return [];
    }
  };

  // Extract all metrics
  const parsedData = {
    // Core Metrics
    conversionProbability: extractNumber(/CONVERSION PROBABILITY:\s*(\d+)%/i),
    confidenceLevel: extract(/CONFIDENCE LEVEL:\s*(High|Medium|Low)/i),
    confidenceReasoning: extract(/CONFIDENCE LEVEL:.*?\n(.*?)(?=\n\n\*\*)/is),

    // Behavioral Score
    behavioralScore: extractNumber(/BEHAVIORAL SCORE:\s*(\d+)\/100/i),
    engagementQuality: extractNumber(/Engagement Quality:\s*(\d+)\/25/i),
    momentum: extractNumber(/Momentum:\s*(\d+)\/25/i),
    stakeholderInvolvement: extractNumber(/Stakeholder Involvement:\s*(\d+)\/25/i),
    timelineProgress: extractNumber(/Timeline Progress:\s*(\d+)\/25/i),

    // Risk Assessment
    riskLevel: extract(/RISK LEVEL:\s*(Green|Yellow|Red|Critical)/i),
    dealHealthTrend: extract(/DEAL HEALTH TREND:\s*(Improving|Stable|Declining|Stalled)/i),
    immediateActionRequired: extract(/IMMEDIATE ACTION REQUIRED:\s*(Yes|No)/i) === 'Yes',

    // Signals
    positiveSignals: extractList('KEY POSITIVE SIGNALS DETECTED:', 'KEY RISK FACTORS'),
    riskFactors: extractList('KEY RISK FACTORS DETECTED:', 'BEHAVIORAL INSIGHTS'),

    // Insights
    behavioralInsights: extract(/BEHAVIORAL INSIGHTS:\s*(.*?)(?=\n\n\*\*)/is),

    // Actions
    recommendedActions: extractList('TOP 3 RECOMMENDED ACTIONS:', 'OPTIMAL NEXT ENGAGEMENT'),

    // Next Engagement
    nextEngagementType: extract(/Type:\s*(Email|Call|Meeting|Demo)/i),
    nextEngagementTiming: extract(/Timing:\s*(.*?)(?=\n)/i),
    nextEngagementFocus: extract(/Message Focus:\s*(.*?)(?=\n)/i),
    nextEngagementGoal: extract(/Goal:\s*(.*?)(?=\n)/i),

    // Forecasting
    forecastingRecommendation: extract(/FORECASTING RECOMMENDATION:\s*(Commit|Best Case|Pipeline|Omit)/i),
    forecastingReasoning: extract(/Reasoning:\s*(.*?)(?=\n\n\*\*)/is),

    // Predictions
    predictedCloseDate: extract(/PREDICTED CLOSE DATE:\s*(\d{4}-\d{2}-\d{2})/i),
    alertPriority: extract(/ALERT PRIORITY:\s*(P0-Critical|P1-High|P2-Medium|P3-Low)/i),

    // Summary
    summaryText: extract(/Summary:\s*(.*?)$/is),

    // Full response for reference
    fullAnalysis: aiOutput,

    // Metadata
    analysisTimestamp: new Date().toISOString()
  };

  // Derived flags for workflows (with null safety)
  parsedData.isHighRisk = parsedData.riskLevel === 'Red' || parsedData.riskLevel === 'Critical';
  parsedData.needsImmediateAttention = parsedData.immediateActionRequired === true;
  parsedData.isStalled = parsedData.dealHealthTrend === 'Stalled';
  parsedData.lowProbability = parsedData.conversionProbability !== null && parsedData.conversionProbability < 50;

  // Add original deal data if available
  if (item.json.dealData) {
    parsedData.dealId = item.json.dealData.dealId;
    parsedData.dealName = item.json.dealData.dealName;
    parsedData.dealAmount = item.json.dealData.amount;
    parsedData.dealStage = item.json.dealData.dealStage;
    parsedData.ownerId = item.json.dealData.ownerId;
  }

  results.push({ json: parsedData });
}

return results;
