// If data is base64 encoded, decode first
const actualBytes = binaryData.data instanceof Buffer 
  ? binaryData.data.length 
  : Buffer.from(binaryData.data, 'base64').length;

return [{
  json: {
    total_bytes: actualBytes,
    media_type: binaryData.mimeType || 'video/mp4'
  },
  binary: {
    data: binaryData
  }
}];

