const cloudName = "{replace with cloud name}";

const rawVideos = [
  $input.first().json.public_id?.[0],
  $input.first().json.public_id?.[1],
  $input.first().json.public_id?.[2]
];

const videos = rawVideos.filter(v => typeof v === "string" && v.trim() !== "");

if (videos.length === 0) {
  return [{ json: { stitched_video_url: null } }];
}

let base = `https://res.cloudinary.com/${cloudName}/video/upload/`;

// Add Twitter-compatible transformations
base += 'vc_h264:baseline:3.0,ac_aac,ar_44100,q_auto:good,f_mp4/';

videos.slice(0, -1).forEach(v => {
  base += `l_video:${v.replace(/\//g, ":")},fl_splice/`;
});

base += `${videos[videos.length - 1].split("/").pop()}.mp4`;

return [{ json: { stitched_video_url: base } }];
