// Get the AI output (string)
const rawOutput = $json.output;

// Safety check
if (!rawOutput || typeof rawOutput !== 'string') {
  throw new Error('No valid AI output found to parse');
}

let parsedOutput;

try {
  parsedOutput = JSON.parse(rawOutput);
} catch (error) {
  throw new Error('Failed to parse AI output as JSON');
}

// Return clean JSON for downstream nodes
return [
  {
    json: parsedOutput
  }
];
