var ALERT_THRESHOLD_DAYS = 20;

var items = $input.all();
var domains = [];

for (var i = 0; i < items.length; i++) {
  var d = items[i].json.Domain;
  if (d && d.trim() !== '' && d !== 'Domain') {
    domains.push(d);
  }
}

return [{ json: { domains: domains, ALERT_THRESHOLD_DAYS: ALERT_THRESHOLD_DAYS } }];

var domains = $input.first().json.domains;
var threshold = $input.first().json.ALERT_THRESHOLD_DAYS;
var output = [];

for (var i = 0; i < domains.length; i++) {
  output.push({ json: { domain: domains[i], threshold: threshold } });
}

return output;

var ALERT_THRESHOLD_DAYS = 20;
var originalItems = $('Split Into Individual Domains').all();
var sslOutputs = $input.all();
var results = [];
var alertDomains = [];

for (var i = 0; i < sslOutputs.length; i++) {
  var domain = originalItems[i].json.domain;
  var output = sslOutputs[i].json.stdout;
  if (!output) output = '';

  var result = {
    domain: domain,
    expiryDate: '',
    daysLeft: '',
    status: '',
    issuer: '',
    lastChecked: new Date().toISOString().split('T')[0],
    error: ''
  };

  if (output.indexOf('notAfter') === -1) {
    result.status = 'ERROR';
    result.error = 'Could not retrieve certificate';
    alertDomains.push(result);
  } else {
    var notAfterMatch = output.match(/notAfter=(.+)/);
    if (notAfterMatch) {
      var expiryDate = new Date(notAfterMatch[1]);
      var now = new Date();
      var daysLeft = Math.floor((expiryDate - now) / (1000 * 60 * 60 * 24));

      result.expiryDate = expiryDate.toISOString().split('T')[0];
      result.daysLeft = daysLeft;

      if (daysLeft <= 0) {
        result.status = 'EXPIRED';
        alertDomains.push(result);
      } else {
        result.status = 'OK';
        if (daysLeft <= ALERT_THRESHOLD_DAYS) {
          alertDomains.push(result);
        }
      }
    }

    var issuerMatch = output.match(/issuer=.*?O\s*=\s*([^,\n\/]+)/);
    if (issuerMatch) {
      result.issuer = issuerMatch[1].trim();
    } else {
      var issuerSimple = output.match(/issuer=(.+)/);
      if (issuerSimple) {
        result.issuer = issuerSimple[1].substring(0, 50);
      }
    }
  }

  results.push(result);
}

return [{
  json: {
    results: results,
    alertDomains: alertDomains,
    hasAlerts: alertDomains.length > 0,
    totalChecked: results.length,
    alertCount: alertDomains.length,
    threshold: ALERT_THRESHOLD_DAYS
  }
}];

var results = $input.first().json.results;
var output = [];

for (var i = 0; i < results.length; i++) {
  output.push({ json: results[i] });
}

return output;

var data = $('Parse SSL Results and Identify Expiring Certs').item.json;
var alertDomains = data.alertDomains;
var threshold = data.threshold;

var certRows = '';
for (var i = 0; i < alertDomains.length; i++) {
  var cert = alertDomains[i];
  var statusColor = '#059669';
  var statusBg = '#d1fae5';
  var statusText = 'OK';

  if (cert.status === 'EXPIRED') {
    statusColor = '#dc2626';
    statusBg = '#fee2e2';
    statusText = 'EXPIRED';
  } else if (cert.status === 'WARNING') {
    statusColor = '#d97706';
    statusBg = '#fef3c7';
    statusText = 'WARNING';
  } else if (cert.status === 'ERROR') {
    statusColor = '#dc2626';
    statusBg = '#fee2e2';
    statusText = 'ERROR';
  }

  var daysLeftColor = '#059669';
  if (cert.daysLeft <= 7) daysLeftColor = '#dc2626';
  else if (cert.daysLeft <= threshold) daysLeftColor = '#d97706';

  certRows += '<tr>';
  certRows += '<td style="padding: 16px; border-bottom: 1px solid #e5e7eb; font-weight: 600; color: #1f2937;">' + cert.domain + '</td>';
  certRows += '<td style="padding: 16px; border-bottom: 1px solid #e5e7eb; text-align: center;">';
  certRows += '<span style="background: ' + statusBg + '; color: ' + statusColor + '; padding: 6px 12px; border-radius: 20px; font-size: 13px; font-weight: 600;">' + statusText + '</span>';
  certRows += '</td>';
  certRows += '<td style="padding: 16px; border-bottom: 1px solid #e5e7eb; text-align: center; font-weight: 700; color: ' + daysLeftColor + '; font-size: 18px;">';
  certRows += cert.error ? 'N/A' : cert.daysLeft;
  certRows += '</td>';
  certRows += '<td style="padding: 16px; border-bottom: 1px solid #e5e7eb; text-align: center; color: #6b7280;">';
  certRows += cert.error ? cert.error : cert.expiryDate;
  certRows += '</td>';
  certRows += '</tr>';
}

var currentDate = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

var emailHtml = '';
emailHtml += '<!DOCTYPE html><html><head><meta charset="utf-8"></head>';
emailHtml += '<body style="margin: 0; padding: 0; background-color: #f3f4f6; font-family: Arial, sans-serif;">';
emailHtml += '<table width="100%" cellpadding="0" cellspacing="0" style="background-color: #f3f4f6; padding: 40px 20px;"><tr><td align="center">';
emailHtml += '<table width="700" cellpadding="0" cellspacing="0">';
emailHtml += '<tr><td style="background-color: #1e3a5f; border-radius: 16px 16px 0 0; padding: 40px 32px; text-align: center;">';
emailHtml += '<h1 style="color: #ffffff; margin: 0 0 8px 0; font-size: 28px;">SSL Certificate Alert</h1>';
emailHtml += '<p style="color: #94a3b8; margin: 0; font-size: 15px;">' + currentDate + '</p>';
emailHtml += '</td></tr>';
emailHtml += '<tr><td style="background-color: #ffffff; padding: 32px;">';
emailHtml += '<table width="100%" cellpadding="0" cellspacing="0" style="background-color: #fef3c7; border-radius: 12px; border-left: 4px solid #f59e0b; margin-bottom: 32px;"><tr><td style="padding: 24px;">';
emailHtml += '<span style="font-weight: 700; color: #92400e; font-size: 18px;">' + alertDomains.length + ' Certificate' + (alertDomains.length > 1 ? 's' : '') + ' Require' + (alertDomains.length === 1 ? 's' : '') + ' Attention</span>';
emailHtml += '<p style="color: #a16207; font-size: 14px; margin: 8px 0 0 0;">Threshold: ' + threshold + ' days before expiration</p>';
emailHtml += '</td></tr></table>';
emailHtml += '<table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom: 32px;"><tr>';
emailHtml += '<td width="32%" style="background-color: #f8fafc; border-radius: 12px; padding: 24px; text-align: center; border: 1px solid #e2e8f0;">';
emailHtml += '<div style="font-size: 36px; font-weight: 700; color: #1e3a5f;">' + data.totalChecked + '</div>';
emailHtml += '<div style="color: #64748b; font-size: 13px; text-transform: uppercase; margin-top: 8px;">Domains Checked</div></td>';
emailHtml += '<td width="2%"></td>';
emailHtml += '<td width="32%" style="background-color: #fef2f2; border-radius: 12px; padding: 24px; text-align: center; border: 1px solid #fecaca;">';
emailHtml += '<div style="font-size: 36px; font-weight: 700; color: #dc2626;">' + data.alertCount + '</div>';
emailHtml += '<div style="color: #64748b; font-size: 13px; text-transform: uppercase; margin-top: 8px;">Alerts</div></td>';
emailHtml += '<td width="2%"></td>';
emailHtml += '<td width="32%" style="background-color: #f0fdf4; border-radius: 12px; padding: 24px; text-align: center; border: 1px solid #bbf7d0;">';
emailHtml += '<div style="font-size: 36px; font-weight: 700; color: #059669;">' + (data.totalChecked - data.alertCount) + '</div>';
emailHtml += '<div style="color: #64748b; font-size: 13px; text-transform: uppercase; margin-top: 8px;">Healthy</div></td>';
emailHtml += '</tr></table>';
emailHtml += '<table width="100%" cellpadding="0" cellspacing="0" style="border: 1px solid #e5e7eb; border-radius: 12px;">';
emailHtml += '<tr style="background-color: #f8fafc;">';
emailHtml += '<th style="padding: 14px 16px; text-align: left; font-weight: 600; color: #475569; font-size: 13px; text-transform: uppercase; border-bottom: 2px solid #e5e7eb;">Domain</th>';
emailHtml += '<th style="padding: 14px 16px; text-align: center; font-weight: 600; color: #475569; font-size: 13px; text-transform: uppercase; border-bottom: 2px solid #e5e7eb;">Status</th>';
emailHtml += '<th style="padding: 14px 16px; text-align: center; font-weight: 600; color: #475569; font-size: 13px; text-transform: uppercase; border-bottom: 2px solid #e5e7eb;">Days Left</th>';
emailHtml += '<th style="padding: 14px 16px; text-align: center; font-weight: 600; color: #475569; font-size: 13px; text-transform: uppercase; border-bottom: 2px solid #e5e7eb;">Expiry Date</th>';
emailHtml += '</tr>';
emailHtml += certRows;
emailHtml += '</table></td></tr>';
emailHtml += '<tr><td style="background-color: #1e293b; border-radius: 0 0 16px 16px; padding: 32px; text-align: center;">';
emailHtml += '<p style="color: #a78bfa; font-weight: 700; font-size: 16px; margin: 0 0 16px 0;">Powered by n8n Automation</p>';
emailHtml += '<p style="color: #94a3b8; font-size: 13px; margin: 0 0 8px 0;">This is an automated SSL monitoring alert.</p>';
emailHtml += '<p style="color: #64748b; font-size: 12px; margin: 0;">Checks run every 3 days to keep your certificates secure.</p>';
emailHtml += '</td></tr></table></td></tr></table></body></html>';

var timestamp = new Date().toLocaleString('en-US', { 
  month: 'short', 
  day: 'numeric',
  hour: '2-digit', 
  minute: '2-digit'
});

var subject = 'SSL Alert: ' + alertDomains.length + ' certificate' + (alertDomains.length > 1 ? 's' : '') + ' expiring soon - ' + timestamp;

return [{ json: { subject: subject, emailHtml: emailHtml, emailBody: emailHtml } }];
