// Filter articles from the last 7 days and limit to 30
const DAYS = 7;
const MAX_ARTICLES = 30;
const cutoff = new Date(Date.now() - DAYS * 24 * 60 * 60 * 1000);

const filtered = $input.all().filter(item => {
  const pubDate = item.json.pubDate || item.json.isoDate;
  if (!pubDate) return true;

  const articleDate = new Date(pubDate);
  return articleDate >= cutoff;
});

// Sort by date (newest first) and take top 30
filtered.sort((a, b) => {
  const dateA = new Date(a.json.isoDate || a.json.pubDate);
  const dateB = new Date(b.json.isoDate || b.json.pubDate);
  return dateB - dateA;
});

return filtered.slice(0, MAX_ARTICLES);

// Standardize fields across different RSS feed formats
return $input.all().map(item => {
  const json = item.json;
  return {
    json: {
      url: json.link || json.url,
      title: json.title,
      description: json.contentSnippet || json.description || json.summary || '',
      pubDate: json.isoDate || json.pubDate,
      source: json.creator || json.author || 'Unknown'
    }
  };
});

// Filter out already-posted articles and pick a random one
const newArticles = $input.first().json.newArticles || [];
const postedData = $input.last().json.postedURLs || [];

// Extract just the URLs from posted data
const postedURLs = postedData.map(item => item.article_url);

// Filter to only unposted articles
const available = newArticles.filter(article => 
  !postedURLs.includes(article.url)
);

if (available.length === 0) {
  // No new articles to process - stop workflow
  return [];
}

// Pick a random article
const randomIndex = Math.floor(Math.random() * available.length);
return [{ json: available[randomIndex] }];

// Always output exactly 1 item to trigger RSS feeds once
// Regardless of how many rows are in the sheet
return [{ json: { ready: true } }];

